/* ***** forum - http://forum.kinozal.tv/forumdisplay...  ************** */

@-moz-document 
     url-prefix("http://forum.kinozal.tv/forumdisplay") ,
     url-prefix("https://forum.kinozal.tv/forumdisplay") 
     
   ,  url-prefix("http://forum-kinozal-tv.appspot.com/forumdisplay") ,
     url-prefix("https://forum-kinozal-tv.appspot.com/forumdisplay")      
{

/* Объявление */     
#threadslist > TBODY > TR:first-child td {
    background-color:  #FFE4E0 !important;
}
/* заголовки таблиц */
/* Раздел 	  Последнее сообщение 	Темы     	Сообщения */
HTML > BODY > DIV:nth-child(3) > DIV > DIV > TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"]:nth-child(9) > THEAD > TR[align="center"] td ,
/* Рейтинг Тема / Автор 	Последнее сообщение Реверс сортировки 	Ответов  */
TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"]#threadslist > TBODY > TR:nth-child(2) td     {
    background-color:  #F6E2A5    !important;  /* F2EEDF */
}

     
/* список тем */   
     
/* last-child */
TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"]#threadslist > TBODY > TR:last-child > td ,
#inlinemodform TABLE#threadslist  TR:last-child > td     {
     background-image: -moz-linear-gradient(to bottom, 
          #F6EBDA  , #fff ) ; 
}      
TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"]#threadslist > TBODY > TR:last-child:hover > td ,
#inlinemodform TABLE#threadslist  TR:last-child:hover > td        
{
     background-image: url("http://forum.kinozal.tv/pic/sbg.gif")   !important;  
     background-repeat: repeat-x  !important;  
     background-size: auto auto  !important; 
}   
TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"]#threadslist > TBODY > TR:last-child:hover ,
#inlinemodform TABLE#threadslist  TR:last-child:hover 
{
     outline: solid #9AD7FD 1px  !important; 
}
     
     
/* таблица в футере */
HTML > BODY > DIV:nth-child(3) > DIV > DIV > FORM:nth-child(13) > TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"] > TBODY > TR:nth-child(2) td ,
HTML > BODY > DIV:nth-child(3) > DIV > DIV > FORM:nth-child(10) > TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"] > TBODY > TR:nth-child(2) td
{
    background:   #F6E2A5  !important; /*  F8DEB1 */
    outline: none !important; 
}  
     
/* Тип сортировки /  Порядок отображения   */
HTML > BODY > DIV:nth-child(3) > DIV > DIV > FORM:nth-child(13) > TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"] > TBODY > TR:last-child > TD:first-child ,
HTML > BODY > DIV:nth-child(3) > DIV > DIV > FORM:nth-child(10) > TABLE[width="100%"][cellspacing="0"][cellpadding="4"][border="0"][align="center"][class="tborder"] > TBODY > TR:last-child > TD:first-child 
{
   background-image: -moz-linear-gradient( top, 
      rgb(241, 210, 156), 
      rgb(261, 230, 176) 15%, 
      #ffe, 
      rgb(261, 230, 176) 85%, 
      rgb(241, 210, 156) ) !important;   
   border: 1px solid #D3AB78 !important;
   vertical-align:   middle !important;
}    
 
     
