    margin-top: 71px !important;
    margin-bottom: 0px !important; 
}


/* лого */
.logo IMG , 
.logo_new IMG , 
IMG[alt="Кинозал.ТВ"]   
{
     visibility:   visible    !important; 
     height: 31px !important;
     min-height: 31px !important;
