}

     
     
/* fix header */
div#header {
     position: fixed !important; 
     z-index: 6000 !important; 
     top: 59px !important;
     margin-bottom: 0px !important; 
     margin-left: 5px !important;          
     width: 100% !important;    /*   99.5%   */
}
     
     
/*  гл. меню */  
#header .menu,     
#header > DIV:nth-child(4) {
   background-image: -moz-linear-gradient( top, 
      rgb(241, 210, 156), 
      rgb(261, 230, 176) 10%, 
      #fff, 
      rgb(261, 230, 176) 90%, 
      rgb(241, 210, 156) ) !important;    
     /* #fdefce  родной цвет фона header =  цвет на лого */
     /*  #f1d29c =  rgb(241, 210, 156)  родной цвет фона menu = родной цвет бордера header */
   font-size: 14px!important;      
}
div#header ,
#header .menu,      
#header > DIV:nth-child(4) {
   border: 3px solid #D3AB78 !important;       
} 
/*  hover- элементы гл. гор. меню */
#header > DIV:nth-child(3) > UL > LI> a:hover ,
#header .menu ul li a:hover     {
    background: #BC3503 !important; 
    color: #FFF !important; 
}     
     
