css:
@-moz-document domain("podio.com") {
.main,
.secondary-header,
.is-selected>a,
.app-view__content,
.js-toggle-items-view,
.frame-content,
.placeholder-row,
.metadata-row,
tr.odd>td,
.unsaved-app-view-bar-component,
.items-section .body .content-row>ul>li.category-field .frame-content>.value>ul>li:not(.selected),
.space-switcher .space-switcher-sidebar,
.left-nav,
.left-nav .org-navigation ul.org-spaces li.regular-space.current>a {
    background: rgb(22, 22, 22) !important;
    /*darkest grey*/
}
.gridster li,
#home_stream,
.grid-tile #space-stream,
#stream-composer,
.dashboard #space_stream,
.show-more,
.app-sidebar,
.report-composer-component__config-section,
.large-text-field-value-component,
.frame-label,
#project-manager .image-block,
.items-section .body .content-row>ul>li.contact-field .frame-content .value,
.framecontent .value,
.field-label,
.relationship-badge-component,
.activity-group,
.activity-group-wrapper,
tr.even>td,
.items-section .body .content-row>ul .contact-card,
.items-section .body .content-row>ul>li.progress-field .progress-wrapper .ui-slider,
.fc-unthemed .fc-today,
.modal-content,
.comment,
.chat-main-wrapper .navigation,
.inner-table-header,
.dashboard .gridster-block ul.content li .inner.table-header,
.items-card>.scrollable table tbody .card-group-body td,
.badge-component .app-badge-content,
.card-item .footer,
.even {
    background: #3E434C !important;
    /*off blue*/
}
.event-tracking,
.header-col,
.items-card>.scrollable .floating .floating-scrollable .inner>div,
.items-card>.scrollable table thead tr th,
.items-card>.scrollable table,
.items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .body {
    color: #ADADAD !important;
}
.event-tracking:hover,
.bd a,
.inner a,
.add-widget-wrapper .centered {
    color: #589DF6 !important;
    /*blue links*/
}
.bd,
.bd h4,
.timestamp,
.content p,
p,
.report-composer-preview-component,
.label,
.badge-component > section,
.badge-component>footer,
.frame-label,
label,
.relationship-badge-component,
th,
tr.odd>td,
tr.even>td,
.datestamp {
    color: rgb(153, 153, 153);
    /*standard text*/
}
.no-footer,
.gridster-widget,
.grid-tile,
.add-widget-wrapper,
.modal-content .navigation,
.footer,
.overflow,
.dialog-footer,
.report-composer-preview-component,
.app-badge-header,
.badge-component > section,
.badge-component>footer,
.items-section .body .content-row>ul>li.can-update .frame-content .value.view-mode:hover,
.items-section .body .content-row>ul.tags-list .can-update:hover,
.items-section .body .content-row>ul.tasks-list.blank-value>li>.frame-wrapper>.frame-content .value:hover,
.relationship-badge-component__title,
.relationship-badge-component__footer,
.activity-header-wrapper,
.activity-header,
.composer-new,
.item,
th,
.app-view-composer__arrow-wrapper,
.app-view-composer__footer,
.input-text-container,
.input-field-container,
.ui-widget-content,
.show-all-comments,
.comment-composer,
.right-nav.chat-pane,
.right-nav.chat-pane .chat-main-wrapper .show-conversation-container,
.right-nav.chat-pane .chat-main-wrapper .show-conversation-container .events-container,
.items-card>.scrollable .floating,
.header-col,
.items-card>.scrollable .floating .floating-scrollable .inner>div,
.items-card>.scrollable table thead tr th,
.items-card>.scrollable table,
.items-card>.scrollable table tbody .card-group-body td .sortable-column .card-item .body {
    background: #3C3F41 !important;
    /*light grey*/
}
.header-title {
    background: none !important;
}
#wrapper,
.footer-global,
.content-container,
.item-overlay,
.fc-day:hover,
li.group,
.ui-state-highlight,
.ui-datepicker-today > .ui-state-highlight {
    background: black !important;
}
textarea,
select,
input,
.token-input-list,
.mce-panel,
#tinymce,
.mce-edit-area,
iframe,
.input-text-composer,
.add-comment {
    background: #2B2B2B !important;
    color: rgb(153, 153, 153) !important;
}
.highlight-red {
    border-left-color: #BC3F3C !important;
}
.overdue .task-border {
    background: #BC3F3C !important;
}
.variable-right {
    background: linear-gradient(to right, rgba(62, 67, 76, 0), #3E434C 15px) !important;
}
.upgrade-section-tier .button-new,
.items-section .body .content-row>ul>li.can-update .frame-content .value.view-mode:hover,
.items-section .body .content-row>ul.tags-list .can-update:hover,
.items-section .body .content-row>ul.tasks-list.blank-value>li>.frame-wrapper>.frame-content .value:hover {
    color: white !important;
}
#space-navigation {
    border-bottom-color: black !important;
}
.silver,
.fc-day-number,
.ui-state-default {
    background: #3C3F41 !important;
    color: white !important;
}
.large-text-field-value-component:not(.is-empty):after {
    background: linear-gradient(to bottom, rgba(62, 67, 76, 0) 0, #3E434C 100%) !important;
}
.category-field-value-component:after,
.navigation-arrow,
.app-view-composer {
    background: none !important;
}
.items-table table tr.even:hover>td,
.items-table table tr.odd:hover>td,
.items-table table tr.ui-selected>td,
.items-table table tr.ui-selecting>td,
.items-table table tr:hover>td {
    background-color: rgb(153, 153, 153) !important;
    color: black !important;
}
li.highlight-green,
.dashboard .gridster-block ul.content li.highlight-green {
    border-left-color: #629755;
}
.fc-agenda-view .fc-event.color-DCEBD8:hover,
.fc-event.all-day.color-DCEBD8:hover,
.fc-event.multi-day.color-DCEBD8:hover {
    background: #629755 !important;
    color: white !important;
}
.fc-event-container:hover a {
    color: white !important;
}
.fc-event-container a,
.fc-agenda-view .fc-event,
.fc-event.all-day,
.fc-event.multi-day {
    color: rgb(153, 153, 153) !important;
}
.conversation-container .events-container ul.events>li>div,
.right-nav .show-conversation-container .events-container ul.events>li>div,
.sidebar-chat .show-conversation-container .events-container ul.events>li>div {
    border-left: none !important;
}
}
