css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("aliexpress.com"), domain("aliexpress.ru")
  
  {

  /********/
 
  .top-banner-container, .home-activity-banner, .home-activity-banner a {height:12px  !important;   transition: height .3s ease;  }
  .top-banner-container:hover {height:80px !important;   transition: height .3s ease;}
  .home-activity-banner:hover, .home-activity-banner a:hover {height:60px !important;  transition: height .3s ease;   }
  
  /********/
   .partner-box, .user-helper-footer,  .wish-list-show, #related-searches, #survey-container, #bp-banner-new,.p4pExpress,.site-server-box, #footer, .shopping-history-show, .recommend-viewed,.me-ad-rotationt,    .ui-fixed-panel-shares, .site-footer ,#magnet-order-list, .footer-copywrite , .mall-floor .title , #meStoreList{opacity:0.1; transition: opacity .4s ease-in-out;}
    .partner-box:hover, .user-helper-footer:hover, .wish-list-show:hover, #related-searches:hover, #survey-container:hover, #bp-banner-new:hover,.p4pExpress:hover,.site-server-box:hover, #footer:hover,  .wish-list-show:hover, .shopping-history-show:hover, .recommend-viewed:hover,.me-ad-rotationt:hover,    .ui-fixed-panel-shares:hover ,.site-footer:hover , #magnet-order-list:hover, .footer-copywrite:hover , .mall-floor .title:hover , #meStoreList:hover, html body.aliexpress div.container.me-body-container div.row div.col-xs-48 a img:hover {opacity:1; transition: opacity .3s ease-in-out;}
  
  
  .mobile-app {transform: matrix(.5,0,0,.5,0,0); opacity:.1; transition:.5s all ease;}
  .mobile-app:hover {transform: matrix(1,0,0,1,0,0);opacity:1;  transition:.1s all ease;}
  .new-user-guide  {height:12px; overflow: hidden;  transition:.6s height ease;}
  .new-user-guide:hover {height:70px; transition:.3s height ease;}
  .me-ad-rotationt, .buyer-activity-bit {transform: matrix(.9,0,0,.9,0,0); opacity:.2; transition:.5s all ease;}
  .me-ad-rotationt:hover, .buyer-activity-bit:hover {transform: matrix(1,0,0,1,0,0);opacity:1;  transition:.2s all ease;}
  .recommend-viewed{transform: matrix(1,0,0,1,0,12); opacity:.2; transition:.5s all linear;}
  .recommend-viewed:hover {transform: matrix(1,0,0,1,0,0);opacity:1;  transition:.1s all linear;}
 
  
  /*********************/ 
  body .page.page-bg-1, .page.page-bg-1, body .page.page-bg-3, .page.page-bg-3, page page-bg-2, .page.page-bg-2 {background-image:none;}
    .page.page-bg-1:after, .page.page-bg-3:after, page-bg-2:after   {}
  
}
