css:
@namespace url(http://www.w3.org/1999/xhtml);

/* 検索結果 */
@-moz-document domain("auctions.search.yahoo.co.jp")
              ,domain("auctions.yahoo.co.jp") {

  body,textarea,select,input {
    font-family: Meiryo !important;
  }

  #header
  { margin: 5px 0 !important; }

  .searchForm-opt2
  { font-size: 90% !important; }

  #SaA,#SaB,#SaC-tg,
  #So4,#ASu,#footer
  { display: none !important; }

  .normalHeader {
      height: 50px !important;
  }

}

/* マイ・オークション - TOP(機能説明) */
@-moz-document domain("user.auctions.yahoo.co.jp") {

  hr {border-color: #FFF; /* 線色 */}

  #modAdSct+center td:first-child
  { font-size: 90%; letter-spacing: -1px; }

  #modMyNavi+table table table table { width: 180px !important; }
  #modSsBox { width: 300px !important; }

  #modAdSct+center+center table table,
  #modInfoBox,#modSaftBox,#modAdLrecArea,
  #modAdLrecArea+*,#modMblBox,#modMyBox+*,
  div.wrapper>center:nth-child(9),
  #modMyBox+div+h2,#modMyBox+div+h2+p,#modMyBox+div+h2+p+div
  {display:none !important;}

}

/* マイ・オークション - ウォッチリスト */
@-moz-document domain("openwatchlist.auctions.yahoo.co.jp")
              ,domain("closedwatchlist.auctions.yahoo.co.jp") {

  hr { border-color: #FFF; }

  body>center:nth-child(9),
  table[bgcolor="#ffe566"],td[bgcolor="a0a0a0"],#modSsBox,
  #modMyNavi+*+*+*+*+*,
  center>hr:first-child,center>hr:first-child+small,
  #modMyNavi+table>tbody>tr>td>table>tbody>tr>td>table:first-child,
  #modMyNavi+table>tbody>tr>td>table>tbody>tr>td>table:first-child+*
  {display:none !important;}

}

/* マイ・オークション - 入札中ほか */
@-moz-document domain("openuser.auctions.yahoo.co.jp")
              ,domain("closeduser.auctions.yahoo.co.jp") {

  hr { border-color: #FFF; }

  body>center:nth-child(9),
  table[bgcolor="#ffe566"],td[bgcolor="a0a0a0"],#modSsBox,
  center>hr:first-child,center>hr:first-child+small,
  #modMyNavi+table>tbody>tr>td>table>tbody>tr>td>table:first-child,
  #modMyNavi+table>tbody>tr>td>table>tbody>tr>td>table:first-child+*
  {display:none !important;}

}
