css:
@-moz-document url-prefix(https://trello.com) {

    * {
        font-weight: 100 !important;
    }
    a {
        text-decoration: none !important;
    }
    body {
        font-family: IranSans,tahoma !important;
        font-size: 8pt;
    }
    .phenom-desc {
        line-height: 25px
    }
    .phenom-meta {
        display: block;
        font-size: 11px;
    }
    .segmented-control-item {
        margin: 5px !important;
    }
    .board-menu-content {
        overflow-y: scroll;
        padding: 15px !important;
    }
    .checklist-item {
        float: right;
        padding: 0 36px 4px 0px;
        direction: rtl;
    }

    .checklist-item .checklist-item-checkbox, .checklist-item.complete .checklist-item-checkbox {
        right: 5px !important;
        left: initial;
    }

    .check-item-text p {
        padding-left: 30px !important;
    }

    .window-main-col .edit textarea.field {
        font-family: IranSans,tahoma !important;
        font-size: 8pt;
        direction: rtl;
        text-align: right;
    }

    .new-task.focus .new-task-text {
        font-family: IranSans,tahoma !important;
        font-size: 8pt;
        direction: rtl;
        text-align: right;
    }

    .card-detail-desc div, .current-comment p, .js-card-desc {
        text-align: right;
        direction: rtl;
    }

    .list-card textarea, .new-comment-input, .js-text, .checklist-new-item-text {
        font-family: IranSans,tahoma !important;
        font-size: 8pt;
        direction: rtl;
    }

    .board-list a {
        font-size: 12px
    }
    #surface, .clearfix, textarea, input {
        font-family: IranSans,tahoma !important;
        direction: rtl !important;
        text-align: right !important;
    }
    input[type="submit"], .card-detail-item-labels .card-label {
        font-family: tahoma;
        font-weight: 100 !important;
    }
    .card-detail-item-labels .card-label {
        font-size: 16px;
        padding-top: 2px;
        height: 33px;
    }
    body {
        background: #f8f8f7 !important;

    }

    h3 {
        font-weight: 300 !important
    }

    .header-woof {
        display: none !important
    }

    #header {
        background: #1E88E5 !important;
        padding: 10px !important;
    }

    .board-header-btn-name-org-logo {
        -webkit-filter: contrast(3000%) invert(100%);
    }

    .js-open-board {

        border: 1px solid #eee;
    }

    .js-open-board:hover {
        border-color: #ddd;
    }

    .board-canvas {
        padding-top: 20px
    }

    .thumbnail {
        background: #979798 !important
    }

    .sidebar-section-header-title {
        text-decoration: none !important;
        font-weight: 300 !important
    }

    #search-text {
        border-radius: 0px !important
    }

    .board-header {
        margin-bottom: 20px !important;
        padding-bottom: 0px !important
    }

    /* header buttons */

    .header-btn.header-notifications,
    .header-btn.header-boards,
    .header-btn.header-search,
    .header-btn.header-login,
    .header-btn.header-signup {
        background: none !important;
    }

    .header-btn:hover {
        background: none !important;
    }

    .header-user {
        top: 10px !important;
        right: 5px !important;
        font-weight: 300 !important
    }

    .board-header {
        height: 40px !important;
        background: #1E88E5 !important;
    }

    .header-btn-text {
        font-weight: 300 !important
    }

    .board-header-btn-text {
        text-decoration: none !important;
    }

    .board-header-btn-name {
        font-weight: 300 !important
    }

    .text {
        text-decoration: none !important;
    }

    .header-member {
        background: none !important;
    }

    .member {
        border-radius: 20px !important;
        border: none !important;
        background: none !important;
    }

    .member img {
        border-radius: 0 !important;
        border: none !important;
    }

    .list-wrapper.placeholder {
        background: #eee;
    }

    .list-area {
        margin-top: 30px !important;
    }

    .list {
        padding: 8px !important;
    }

    .list-header {
        margin: 5px 0px 10px 0px !important;
    }

    .list-header-name {
        color: #1E88E5 !important;
        font-weight: 700 !important;
        text-transform: none;
    }

    .list {
        background: #fff !important;
        border: 1px solid #eee !important;
        border-top: 2px solid #1E88E5 !important;
        border-radius: 0px;
    }

    .list-card {
        border: 1px solid #eee !important;
        padding: 5px !important;
    }

    .active-card {
        background: #fff !important;
        transition: border 0.2s ease-out !important;
        border: 1px solid #1E88E5 !important;
    }

    .list-card-operation {
        background: none !important;
    }

    /* Add list field */

    .js-add-list {
        background: none !important;
        padding: 0px !important;
    }

    .edit-controls {
        margin-top: 8px !important;
    }

    .js-add-list:hover {
        color: #1E88E5 !important;
    }

    .js-add-list span {
        color: #bbb !important;
    }

    .js-add-list span:hover {
        color: #1E88E5 !important;
    }

    .list-name-input {
        background: #fff !important;
        border: 1px solid #eee !important;
        padding: 12px !important;
        box-shadow: none !important;
    }

    /* Add card field */

    .open-card-composer {
        color: #999 !important;
    }

    .open-card-composer:hover {
        text-decoration: none !important;
        background: none !important;
        color: #1E88E5 !important;
    }

    .js-composer:hover {
        border: 1px solid #eee !important;
    }

    .window-overlay {
        /* blue background: rgba(30, 136, 229, 1) !important; */
        background: rgba(255, 255, 255, 0.8) !important;
    }

    strong {
        font-weight: 100 !important
    }

    .js-open-move-from-header {
        color: #F45B5B !important;
        font-weight: 300 !important
    }

    .window-title {
        margin-top: 20px;
    }

    .window-title-text {
        font-weight: 300 !important;
        color: #1086E8 !important;
    }

    .button-link {
        font-weight: 300;
    }

    .button-link:hover {
        background: #1976D2 !important;
        border-color: #1565C0 !important;
    }

    .attachment-add-link-submit {
        background: #1E88E5 !important;
        box-shadow: none !important;
        font-weight: 700 !important;
        color: #fff !important;
    }

    /* Primary button */

    input.primary {
        background: #1E88E5 !important;
        box-shadow: none !important;
    }

    input.primary:hover {
        background: #1976D2 !important;
    }

    input.primary:disabled {
        background: none !important;
        border: 1px solid #eee;
        color: #ddd !important;
    }

    /* Cancel icon button */

    .js-cancel {
        margin-left: 4px;
    }

    .ui-state-active {
        background: #F45B5B !important
    }

    /* ------------------- */
    /* --- CARD DETAIL --- */
    /* ------------------- */

    .window {
        background-color: #1E88E5;
        box-shadow: 0 19px 38px rgba(0, 0, 0, 0.30), 0 15px 12px rgba(0, 0, 0, 0.22);
        border-radius: 0px;
    }

    .dialog-close-button {
        margin: 12px 12px 0px 0px !important;
    }

    .window-wrapper {
        margin-top: 4px;
        background: #fff !important;
    }

    .window-wrapper a {
        text-decoration: none !important
    }

    /* Window header */

    .window-header {
        margin-top: 0px;
    }

    .window-header-icon {
        display: none !important;
    }

    .window-title-text {
        font-weight: 700 !important;
    }

    .field {
        box-shadow: none !important;
    }

    /* hide 'in Liste bla' */

    .window-header-inline-content {
        display: none;
    }

    /* label */
    .card-label {
        text-shadow: 1px 1px 0px rgba(0, 0, 0, .2), 0 0 0px #000;
    }

    /* card description */

    .card-detail-item-header-edit {
        display: none !important;
    }

    .card-detail-item-header {
        display: none !important;
    }

    .card-detail-item {
        margin-top: 12px;
    }

    /* separators */

    .card-detail-item {
        margin-bottom: 40px;
    }

    .window-module {
        margin-bottom: 40px;
    }

    /* section titles */

    .window-module-title-icon {
        display: none !important;
    }

    h3 {
        font-weight: 300 !important;
        color: #000;
    }

    .checklist-progress-bar-current {
        background: #1E88E5 !important;
    }

    /* comments */

    .action-comment {
        box-shadow: none !important;
        border-left: 2px solid #1E88E5;
        border-radius: 0px !important;
        margin-top: 12px;
        margin-bottom: 12px;
    }

    .phenom {
        border-bottom: 1px solid #eee;
    }

    .window-module-title {
        border-top: 2px solid #1E88E5;
        padding: 14px 0px 18px 0px;
    }

    /* ------------------- */
    /* --- BOARD MENU --- */
    /* ------------------- */

    .board-menu {
        background: #fff !important;
    }

    /* ----------------------- */
    /* --- BOARDS SWITCHER --- */
    /* ----------------------- */

    .boards-drawer {
        box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16);
        padding: 0px 24px;
    }

    .boards-drawer input {
        margin: 6px 0px;
    }

    input.js-search-boards {
        border: none;
        border-bottom: 1px solid #ddd;
        background: none;
        border-radius: 0px;
        padding: 0px;
        margin-top: 12px;
        margin-bottom: 24px;
    }

    input.js-search-boards:hover {
        border-color: #1E88E5;
    }

    input.js-search-boards:focus {
        border-color: #1E88E5;
        box-shadow: none;
    }

    .js-board-drawer-buttons {
        margin: 36px 0px;
        margin-bottom: 12px;
        border-top: 2px solid #1E88E5;
    }

    .sidebar-section-header-toggle-icon:hover {
        background: none;
    }

    .quiet-button:hover {
        background: none;
        color: #1E88E5;
    }

    .boards-drawer .empty, .boards-drawer .quiet-button {
        margin: 12px 0px;
        padding: 0px;
    }

    .sidebar-section-header {
        margin: 1pc 30px 10px 0px;
        position: relative;
    }
    .list-wrapper:first-child, .list-wrapper {
        margin: 10px 5px 20px 5px !important;
        vertical-align: top;
        height: 350px;
    }
    #board {
        overflow-y: auto !important;
        padding: 10px !important;
        margin-bottom: 0px !important;
        white-space: normal;
        text-align: center;
    }
    .list-wrapper {
        display: inline-block;
        text-align: right;
    }
    .board-header {
        margin: 0px !important;
    }
    .placeholder.js-open-add-list {
        background: #f1f1f1;
    }
    .board-menu.js-fill-board-menu {
        top: 49px !important;
    }
    .board-menu-container {
        right: 0px !important;
    }
    .board-menu-header.js-board-menu-title.is-board-menu-default-view.is-in-frame {
        background: #f1f1f1;
        border-bottom: 1px solid rgb(204, 204, 204);
    }
    .board-menu-header.js-board-menu-title.is-board-menu-default-view.is-in-frame hr {
        display: none;
    }
    .board-wrapper.is-show-menu .board-main-content {
        margin-right: 0px !important;
    }
    .board-wrapper.is-show-menu .board-header-btn.mod-show-menu {
        margin-right: -170px !important;
        opacity: 0 !important;
    }
    .open-card-composer.js-open-card-composer {
        background: rgb(241, 241, 241) none repeat scroll 0% 0%;
        border-radius: 0px;
        padding-top: 13px;
        direction: ltr;
        text-align: left;
        margin-top: 10px;
    }
    .open-card-composer.js-open-card-composer:hover {
        background: rgb(241, 241, 241) !important;
    }
    .list-card-labels .card-label {
        float: right !important;
        height: 10px !important;
        width: 30px !important;
    }
    .boards-page-board-section-header-icon {
        right: -30px !important;
        left: auto !important;
    }
    .boards-page-board-section-header-name {
        margin-left: 10px !important;
    }
    .board-list-item-name {
        padding-right: 20px;
    }
    .details.has-sub-name {
        padding-right: 0px !important;
    }
    .list-card-operation {
        left: 0px !important;
        right: auto !important;
    }
    .list-card-title.js-card-name {
        line-height: 25px !important;
    }
    /* ----------------------- */
    /* --- BOARDS SWITCHER --- */
    /* ----------------------- */

    .js-add-board {
        color: #999 !important;
        background: #fff !important;
        border: 1px solid #eee !important;
    }

    .js-add-board:hover {
        transition: border 0.2s ease-out !important;
        border: 1px solid #1E88E5 !important;
    }
}
