css:
@-moz-document url-prefix("https://www.reddit.com/") {

/* Header color */
.res-nightmode #header
{
    background-color: #555 !important;
}

/* Primary background color */
.res-nightmode body,
.res-nightmode body div.content,
.res-nightmode div.side,
.res-nightmode div.titlebox,
.res-nightmode div.titlebox div.usertext-body *,
.res-nightmode .sr_style_toggle,
.res-nightmode .flairtoggle,
.res-nightmode .leavemoderator,
.res-nightmode h1.redditname,
.res-nightmode .footer-parent
{
    background-color: #191919 !important;
}

/* RES Shortcuts Editor (top right buttons) */
.res-nightmode #RESShortcutsEditContainer,
.res-nightmode #RESShortcutsEditContainer *
{
    background-color: #191919 !important;
}

/* Top subreddit bar/header */
.res-nightmode #sr-header-area
{
    background-color: #191919 !important;    
}
.res-nightmode #sr-header-area *
{
    color: #999 !important;
}

/* Active item bg color */
.res-nightmode .RES-keyNav-activeElement,
.res-nightmode .RES-keyNav-activeElement .md-container
{
    background-color: #202020 !important;
}

/* Unvisited link color */
.res-nightmode .thing .title
{
    color: hsl(0, 0%, 87%);
}

/* Stickied link color */
.res-nightmode .thing.stickied .title
{
    color: hsl(0, 0%, 87%) !important;
}

/* Visited link color */
.res-nightmode .tabmenu a,
.res-nightmode #header-bottom-right a,
.res-nightmode .tabmenu a:visited,
.res-nightmode #header-bottom-right a:visited
{
    color: rgb(140, 179, 217);
}
.res-nightmode .thing .title:visited,
.res-nightmode .thing.visited .title,
.res-nightmode .combined-search-page .search-result a:visited,
.res-nightmode .combined-search-page .search-result a:visited > mark
{
    color: hsl(0, 0%, 65%);
}

/* Hide 'daily reddit gold goal' */
.res-nightmode div.goldvertisement
{
    display: none;
}

/* Make thumbnails less strong */
.res-nightmode a.thumbnail.nsfw,
.res-nightmode a.thumbnail.self
{
    opacity: 0.3 !important;
}

/* 'Create your own subreddit' box */
.res-nightmode .side ul.content,
.res-nightmode body .side .sidebox.create,
.res-nightmode body .side .sidebox.create *
{
    background-color: #191919 !important;
    color: #ddd;
}

/* 'Submit a new link/text post' buttons */
.res-nightmode .sidebox.submit,
.res-nightmode .sidebox.submit *
{
    background-color: #191919 !important;    
}
.res-nightmode .sidebox.submit *
{
    color: #ccc !important;
}

/* Extra padding between moderator names, etc. */
.res-nightmode .sidecontentbox ul.content li
{
    margin: 4px;
}

/* Link coloring for moderator names, etc. */
.res-nightmode .sidecontentbox ul.content a
{
    color: #aaa !important;
}

/* /r/toolbox */
.res-nightmode #moderation_tools ul li a
{
    background-color: rgba(0, 0, 0, 0) !important;
    color: #aaa;
}

/* /r/toolbox */
.res-nightmode a.tb-bracket-button
{
    background-color: rgba(0, 0, 0, 0) !important;
    border: none !important;
    font-weight: bold;
}

/* /r/toolbox */
.res-nightmode .tb-action-button
{
    background-color: #000 !important;
}

/* 'hot', 'new', 'rising' tabs */
.res-nightmode ul.tabmenu *
{
    color: #aaa !important;
}

.res-nightmode div.menuarea
{
    border: none;
}

/* Karma, settings, etc. */
.res-nightmode #header-bottom-right *
{
    color: #bbb !important;
}

/* Never Ending Reddit page separator */
.res-nightmode .NERPageMarker
{
    border: none;
}

/* Left slide-out panel on the frontpage */
.res-nightmode .listing-chooser, 
.res-nightmode .grippy, 
.res-nightmode .grippy::before, 
.res-nightmode .grippy::after
{
    background-color: #222 !important;
}

/* Remove ugly backgrounds from the overview sidebar */
.res-nightmode .gold-accent,
.res-nightmode .server-seconds
{
    background-color: rgba(0, 0, 0, 0) !important;
    border: none;
}
}
