css:
@-moz-document url(http://www.csgodouble.com) {
    body {
    background-color: #25242c;
    color: #ec5c4c;
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
}

#banner {
    color: #f5f5f5;
    font-size: 18px;
    font-weight: 700;
    left: 0px;
    line-height: 50px;
    position: absolute;
    text-align: center;
    width: 100%;
}

#pullout {
    background-color: #25242c;
    border-right: 1px solid #1e1d24;
    bottom: 0px;
    left: 50px;
    position: absolute;
    top: 50px;
    width: 400px;
}

#sidebar {
    background-color: #25242c;
    border-right: 1px solid #1e1d24;
    bottom: 0px;
    left: 0px;
    position: absolute;
    top: 50px;
    width: 50px;
}

.btn-default {
    background-image: linear-gradient(to bottom, #232129 0%, #25242c 100%);
    background-repeat: initial;
    border-color: #1e1d24;
    box-shadow: inset 0 1px 0 rgba(0, 0, 0, 0.15), 0 1px 1px rgba(0, 0, 0, 0.075);
    color: #ec5c4c;
    text-shadow: 0 1px 0 #212027;
}

.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active {
    background-color: #212027;
}

.chat-link, .chat-link:hover, .chat-link:active {
    color: #f5f5f5;
}

.divchat {
    background-color: #212027;
    border-bottom: 1px solid #1e1d24;
    height: 500px;
    overflow-y: auto;
    padding: 5px;
    resize: none;
    word-wrap: break-word;
}

.form-control {
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    background-color: #25242c;
    background-image: none;
    border: 1px solid #1e1d24;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    color: #f5f5f5;
    display: block;
    font-size: 14px;
    height: 34px;
    line-height: 1.42857143;
    padding: 6px 12px;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    width: 100%;
}

.list-group-item {
    background-color: #232129;
    border: 1px solid #1e1d24;
    display: block;
    margin-bottom: -1px;
    padding: 10px 15px;
    position: relative;
}

.my-row {
    border-bottom: 1px solid #1e1d24;
    font-size: 18px;
    font-weight: bold;
    padding: 10px;
}

.navbar-default {
    background-color: #25242c;
    border-color: #1e1d24;
}

.navbar-default .navbar-brand {
    color: #ec5c4c;
}

.navbar-default .navbar-nav>li>a {
    color: #ec5c4c;
}

.panel {
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,.05);
    background-color: #232129;
    border: 1px solid transparent;
    border-radius: 4px;
    box-shadow: 0 1px 1px rgba(0,0,0,.05);
    margin-bottom: 20px;
}

.panel-default>.panel-heading {
    background-color: #212027;
    border-color: #1e1d24;
    color: #f0f0f0;
}

.progress {
    background-color: #1e1d24;
}

.progress-bar-danger {
    background-color: #c9302c;
}

.side-icon.active {
    background-color: #25242c;
}

.total-row {
    border-left: 1px solid #1e1d24;
    border-right: 1px solid #1e1d24;
    font-size: 18px;
    font-weight: bold;
    padding: 10px;
}

.well {
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
    background-color: #212027;
    border: 1px solid #1e1d24;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
    margin-bottom: 20px;
    min-height: 20px;
    padding: 19px;
}



}
