	@font-face {
		font-family: FiraCode;
		font-style: normal;
		font-weight: normal;
		src: url("https://cdn.rawgit.com/tonsky/FiraCode/master/FiraCode-Regular.otf") format("opentype");
	}
	
	.blob-code-inner, pre {
		font-family: FiraCode, Consolas, "Liberation Mono", Menlo, Courier, monospace;
	}
}
