css:
@-moz-document domain("hyunsdojo.com") {
body {
    background: url('https://www.xmple.com/wallpaper/black-green-graph-paper-grid-1920x1080-c2-000000-228b22-l2-3-96-a-0-f-20.svg') 0 0;
    }
.front_submit,
.ptl_submit,
.post_error,
.ptl_up_btn {
    background: linear-gradient(to bottom, rgb(125, 30, 30) 0%, rgb(224, 143, 199) 100%);
    }
#profile_top_footer {
    background: linear-gradient(to bottom, #FFF 0%, #FFF 65px);
    }
.forum_cat,
.ptl_title,
#ptl_menu .front_menu_a.label,
#front_menu .front_menu_a.label,
#chat_tabs .chat_tab .chat_new_box .chat_box_title,
.button,
.right_cat,
.usr_cat,
input[type=submit],
button,
#chat_tabs .chat_tab,
.more_info h3,
.ptl_cat,
dd input,
.submit-buttons input,
.box_header,
.box_footer {
    background: linear-gradient(to bottom, #000 0%, #000 100%);
    }
.paginate,
.paginate_btn {
    background: linear-gradient(to bottom, rgb(212, 203, 190) 0%, rgb(236, 227, 219) 100%)!important;
    }
.forum_row_icon {
    background-image: url(http://orig11.deviantart.net/398e/f/2016/012/c/6/38x38heart_by_chaoshedgust-d9no1gu.png);
    background-repeat: no-repeat;
    }
.forum_row_icon img {
    width: 0px }
.quotetitle,
.attachtitle,
.codetitle {
    border-color: #BFBB9C;
    background-color: #BFBB9C;
    }
}
