css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.rage3d.com/board/") {
td[id^=td_post] iframe {
    border: none !important;
    width: 745px;
    height: 464px;
    margin-top: 3rem;
    margin-left: 5%;
}

td[id^=td_post] div[class*=KonaBody] {
    font-size: 1.08rem;
}

/* Quoted Post */
td[id^=td_post] .alt2 {
    border-width: 1px !important;
    border-style: solid !important;
    border-color: rgba(80,80,80,0.5) !important;
    font-size: 1.03rem;
}

td[id^=td_post] .alt2 img,
td[id^=td_post] .alt2 iframe {
    transition: all 0.5s;
    -webkit-transition: all 0.5s;
    -moz-transition: all 0.5s;
}

td[id^=td_post] .alt2 iframe {
    width: 112px;
    height: 70px;
    margin-top: 0.8rem;
    margin-left: 2%;
}

td[id^=td_post] .alt2 iframe:hover {
    width: 745px;
    height: 464px;;
    margin-top: 3rem;
    margin-left: 5%;
}

td[id^=td_post] .alt2 img {
    width: 5%;
    height: auto;
}


td[id^=td_post] .alt2 img:hover {
    width: 85%;
    height: auto;
}


td[id^=td_post] .alt2 img.inlineimg {
    width: auto;
    height: auto;
    
}

/* Quotes in Signatures */
div[id^=post_message] + div div  {
    margin-bottom: 0.25rem !important;
}

div[id^=post_message] + div .smallfont {
    opacity: 0.15;
}

div[id^=post_message] + div .alt2 * {
    width: 75%;
    font-size: 0.75rem;
}


/* Thread List */
td[id^=td_threadtitle_] a {
    font-size: 1.07rem !important;
}
}
