css:
@-moz-document
regexp("(https|http)://(www|accounts|books|docs|drive|encrypted|images|news|play|plus|translate).google\\.(com|([a-z]{2})).*"), /*Global*/
url-prefix(http://blogsearch.google),
url-prefix(http://video.google)
{
BODY{background-color:#000; color:#fff;}

A{color:#00f;}
A:visited{color:#ff0;}
CITE{border-color:#0f0;}
SPAN{border-color:#ff0;}
H2{border-color:#0f0;}
P{border-color:#0f0;}
DIV{border-color:#0f0;}

A:hover{background-color:#000; color:#0f0;}
DIV.s:hover{background-color:#888;}

BUTTON{border-color:#0f0;}
INPUT{background-color:#000; border-color:#000; color:#0f0;} 


DIV#logocont{background-color:#FFF;} 										/*Logo background*/
DIV#hplogo{background-color:#FFF;} 										/*Logo background (main page)*/
DIV#gb.gb_T{background-color:#FFF;}				/*Top bar (main page)*/


DIV.sbib_b{background-color:#4285F4;}										/*Search form*/
DIV.tsf-p{background-color:#fff;} 								/*Search form outer fill */
SPAN.crt-p{background-color:#F0F;}
DIV.sfbgg{background:linear-gradient(#000,#888,#000); color:#000;}			/*Search bar*/
DIV#hdtbSum{background:linear-gradient(#000,#fff,#000); color:#000;}		/*Tools bar*/
DIV#topabar{background:linear-gradient(#888,#fff,#888); color:#000;}		/*Results bar*/
DIV.fbar{background:linear-gradient(#888,#fff,#888); color:#000;}			/*Help bar*/
DIV#ires{background:linear-gradient(to right,#FFF,#AAA,#FFF); color:#000;}	/*Results list*/
DIV#center_col{background-color:#4285F4;}							/*Results list outer border*/
DIV#res.med{background-color:#FFF;}								/*Results list inner border*/
DIV#footcnt{background-color:#f00;}
DIV.gb_jc{background-color:#888;}
DIV.srg{background:linear-gradient(left,#000,#fff,#0ff); color:#000;}
DIV.sfbg.nojsv{background-color:#8f8;}
DIV#brs{background:linear-gradient(to right,#000,#000,#000); color:#000;}
div#cnt{background-color: #888; background-image:url(http://cdn.images.motherlessmedia.com/images/963D728.jpg); background-position:center right; background-repeat:no-repeat;}
DIV#navcnt{background-color:#088;}
SPAN.gb3{background-color:#f00;}
SPAN.gb4{background-color:#fff; color:#000;}
UL.gb_ja.gb_ca{background-color:#000;}

/*Google Images*/

A#irc_cb{background-color:#F00;}	/* Close Button (Image Search) */
DIV.irc_mic{background-color:#080;}	/* Zoom Image Background (Image Search) */
A#irc_la{background-color:#0f0;}
A#irc_ra{background-color:#0f0;}
DIV.irc_cc{background-color:#fff;}
DIV.irc_c{background-color:#000;}

DIV.irc_sep{background-color:#0f0;}
DIV.irc_b{background-color:#000;}

/*Google Translate*/
DIV#gt-appbar{background-color:000;}
DIV#gt-apb-main{background-color:000;}
DIV#gt-text-c{background-color:000;}
DIV#gt-text-top{background-color:000;}
TEXTAREA#source.goog-textarea.short_text{background-color:000;}
SPAN#result_box.short.text{background-color:000;}
SPAN.hps{background-color:fff; color:0f0;}
DIV#gt-ft-mkt{background-color:000;}


TEXTAREA{background-color:#000; border-color:#0f0; color:#FFF;}

TABLE{background-color:#000; color:#fff;}
TR{background-color:#000;}
TH{border-color:#0f0;}

LI{color:#000;}
}
