css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("hearthpwn.com"), domain("diablofans.com"), domain("heroesnexus.com"), domain("overpwn.com"), domain("mmo-champion.com") {

  /* Hide the classes */
  .t-hot-decks,
  .t-top-builds,
  .newest-heroes-container{
    display: none !important;
  }
    
  /* Hide the one thing thats using an Id instead*/
  #hot-builds{
    display: none !important;
  }
  
  /* Hide various From the Forums bars */
  
  /*MMO Champion*/
  .recent:nth-child(2){
    display: none !important;
  }
  
  /*Diablofans*/
  .mobile-view-2.selected{
    display: none !important;
  }
  
  /*overpwn*/
  .primary-content>.mobile-view-2{
    display: none !important;
  }
  
  /*Rest*/
  .recent-discussion{
    display: none !important;
  }
}
