css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("topachat.com") {
  
  html, button, input, select, textarea {
    color: whitesmoke;
    background-color: black;
  }
  
  a {
    color: #81CFE0;
  }
  
  #content {
    border: none;
  }
  
  nav ul, nav ol, ul.unstyled, ol.unstyled {
    background-color: #6C7A89;
  }
  
  .megamenu .panels, .megamenu .global {
    background: black;
    color: whitesmoke;
  }
    
  #primary-nav .megamenu a, .megamenu .col, .megamenu .global, .megamenu.col4-guides .panel, .megamenu.col5 .panel, .megamenu.col3-guides .panel, .megamenu.col3 .panel {
    background-color: black;
    color: whitesmoke;
  }
  
  #primary-nav > ul > li > a {
    color: whitesmoke;
  }
  
  .ariane li.current {
    color: whitesmoke;
    background: #6C7A89;
  }
  
  #primary-nav {
    background: black;
    border: none;
  }
  
  #primary-nav ul {
    border: none;
  }
  
  #primary-nav ul li {
    border: none;
  }
  
  .megamenu .more-categories .titre {
    color: whitesmoke;
    background-color: black;
  }
  
  #produits-home .top-list a.produit-big .preview {
    background: #252C33;
  }
  
  #produits-home .top-list a.produit-big .libelle {
    background: black;
    color: whitesmoke;
  }
  
  #produits-home .top-list a.produit, #produits-home .top-list a.produit-big {
    background-color: black;
    color: whitesmoke;
  }
  
  #categories .listProduct ul {
    background: none;
  }
  
  #categories .listProduct .SUPGAMME span, #categories .listProduct .GAMME span {
    background: #252C33 none repeat scroll 0% 0%;
  }
    
  #categories .listProduct .CATEGORY, #categories .listProduct .GAMME a {
    background-color: black;
  } 
  
  #guides-assistants {
    border: none;
    background: black;
  }
  
  #guides-assistants li {
    background: #252C33;
  }
  
  #guides-assistants li:hover {
    background: grey;
  }
  
  #top-marques {
    border: none;
    background: black;
  }
  
  .products-list .mode-affichage, .products-list .filtres-texte, .filtres {
    background: #252C33;
    color: whitesmoke;
  }
  
  .product-sheet, .cart-box {
    background-clip: #333;
    color: whitesmoke;
  }

  .produits .grille-produit a {
    color: whitesmoke;
  }
  
  .NEW .bandeau-anico {
    color: black;
  }
  
  .presentation-texte .more_0 {
    background: none;
    color: whitesmoke;
  }
  
  label.combo, [data-type-filter="combo"] {
    background: #252C33;
    /*border: none;*/
  }
  
  .filter-box .ruler .data {
    background: none;
    text-align: center;
    border: none;
  }
  
  .filter-box [data-type-filter="combo"] div.checked {
    background: #6C7A89;
  }
  
  .filter-box .ruler {
    background: #6C7A89 none repeat scroll 0% 0%;
    border: none;
  }
  
  .product-sheet, .detail2 #content {
    background-color: #252C33;
    color: whitesmoke;
  }
  
  .product-sheet .product-preview .thumbnails {
    border: 1px solid #FFF;
    background-color: white;
  }
  
  .cart-box {
    background-color: #14171A;
    border: none;
  }
  
  .button.cart, input.cart[type="submit"] {
    box-shadow: initial;
  }
  
  .cart-box .prix, .cart-box .panier, .cart-box .share {
    border-bottom: none;
  }
  
  dl.tabs {
    border-bottom: initial;
    background-color: #6C7A89;
    color: whitesmoke;
  }
  
  dl.tabs dd a {
    color: whitesmoke;
  }
  
  dl.tabs dd {
    margin-top: initial;
    border-radius: initial;
    background-image: none;
    border-width: initial;
    border-style: initial;
    border-color: initial;
    border-image: none;
    background-color: #6C7A89;
    color: whitesmoke;
    font-size: 22px;
    font-weight: bold;
  }
  
  dl.tabs dd:hover {
    position: relative;
    top: initial;
    padding-bottom: initial;
    background-image: initial;
    background-color: #95A5A6;
  }
  
  dl.tabs dd.active {
    background-image: none;
    position: relative;
    top: initial;
    padding-bottom: initial;
    border: initial;
  }
  
  dl.tabs dd.active a {
    color: whitesmoke;
    background-color: #14171A;
  }
  
  ul.tabs-content.contained > li {
    background: #252C33 none repeat scroll 0% 0%;
    border: none;
  }
  
  ul.tabs-content.contained > li section {
    background: #252C33;
    color: whitesmoke;
  }
  
  .wysiwyg-color-blue {
    color: whitesmoke;
  }
  
  .caracLine:nth-of-type(2n) {
    background: #656D75;
  }
  
  #servicesTab th {
    background: #252C33;
  }
  
  dl.tabs.bottom {
    border-top: none;
  }
  
  dl.tabs.contained.bottom {
    display: none;
  }
  
  #site-footer .nouveautes {
    display: none;
  }
  
  #authentification input[type="email"], #authentification input[type="password"] {
    border: none;
    background-color: gray;
  }
  
  input:invalid, textarea:invalid {
    background-color: #96281B;
  }
  
  #avis article {
    background-color: #1C2126;
  }
  
  #caddie table,
  .currQt,
  #caddie tr:nth-of-type(2n) {
    background: #252c33;
  }
  
  .reveal-modal {
    background: #14171a;
    border: 12px solid #ffffff;
  }
  
  .module {
    background: #252c33;
    border: none;
  }
  
  .module .selected {
    background: #343C45;
    color: whitesmoke;
  }
  
  .address .content {
    color: whitesmoke;
  }
  
  .module .address, .module .shipping-mode {
    background: #14171a;
  }
  
  #boxCatGamme {
    background: #39424D;
  }
  
  #caddie td, #caddie th {
    /*background: #252c33;*/
  }
  
  #config-maker, #config-preview {
    border: initial;
    background: #000;
  }
  
  .select-compo, .list-compos .best-seller, .cfgo-nbref-type {
    background: initial;
  }
  
  .list-compos.open {
    background: black;
  }
  
  .list-compos tr {
    background: black;
  }
  
  .list-compos tr:nth-of-type(2n) {
    background: #252c33;
  }
  
  .no-touch .list-compos tr:hover {
    background: #181d20;
  }
 
}
