css:
@-moz-document
url-prefix(https://userstyles.org)
{
BODY{background-color:#222; color:#fff;}
HEADER#main-header{background:linear-gradient(#888,#FFEC8B,#888); color:#000;}


NAV{background-color:#FFEC8B;}
UL{background-color:#000;}

div#show-advanced-search{background-color:#000;}
div#left-sidebar{background-color:#000;}
div#style_info{background-color:#888;}
FOOTER#related{background-color:#088;}

A{color:#A063EC;}
A:hover{background-color:#000; color:#0f0;}
A:visited{color:#A6E;}
SPAN{border-color:#ff0;}
H2{border-color:#0f0;}
P{border-color:#0f0;}
DIV{border-color:#0f0;}

BUTTON{border-color:#0f0; color:#fff;}
INPUT{background-color:#000; border-color:#0f0; color:#0f0;}
INPUT[type="submit"]{background:linear-gradient(#000,#fff,#000); border-color:#888; color:#000;}

TEXTAREA{background-color:#000; border-color:#0f0; color:#FFF;}

TABLE{background-color:#000; color:#fff;}
TR{background-color:#000;}
TH{border-color:#0f0;}

LI{color:#fff;}
LI#taw0.taw.taw0{background-color:#000; color:#000;}

PRE:hover{background-color:#0F0; color:#000;}
DIV.CodeMirror-lines{background-color:#000; color:#0F0;}
IMG.img_ad{opacity:0.1;}
A#aw0{opacity:0.1;}
INS{opacity:0.1;}
}
