html, body {
    	max-width: 100%;
	overflow-x: hidden;
	background-color: #f5f5f5;
}

.mission_vehicle_state {
	position: relative;
	margin-top: -60px;
	margin-left: -20px;
	height: 33px !important;
	width: 30px !important;
}

.glyphicon {
	font-size:11px;
	line-height: 2;
}

.missionSideBarEntry {
	margin-bottom: 10px;
	margin-top: 10px;
	border: 10px;
	border-color: #34495e;
	border-bottom: solid;
	background-color: #f5f5f5;
	border-radius: 5px;
}

.panel-heading, .panel, .panel-body {
	border: none;
	box-shadow: none;
}

.panel-heading {
	background-image:none !important;
	/*background-color: #e74c3c !important;*/
	color: #34495e !important;
}

.panel{
	background-image: none !important;
	/*background-color: #bdc3c7 !important;*/
	color: #2c3e50 !important;
	border-radius: 10px;
	border-top: solid;
	border-bottom:solid;
	border-color: #34495e !important;
}

.missionSideBarEntry .panel-body {
	background-color: #f5f5f5;
}

.missionSideBarEntry .panel {
	border-bottom: none !important;
}

.map_position_mover { 
	color: #2c3e50 !important;
}

.navbar-default, .dropdown-menu {
	background-image: none !important;
	background-color: #e74c3c;
	border-radius: 0;
	border: 5px;;
	border-bottom: solid;

}

.btn, .progress, .alert {
	background-image: none !important;
	border: none;
	border-radius: 5px;
}

.progress-bar-striped, .progress-striped .progress-bar {
	background-size: 20px 20px;
	height: 15px;
}

.progress {
	background-color: #486683;
	height: 15px;
}

.small .progress {
	height: 6px;
}

#map_outer {
	margin-top: -20px;
}

.progress-bar-danger {
	background-color: #e74c3c;
}

.col-xs-11 {
	width: 100% !important;
	margin-top: -20px;
	position: relative;
}

/* Einsatzliste länge */
#missions-panel-body {
	height: 1000px !important;
}

#missions_outer {
	margin-left: -10px;
	margin-top: -10px;
}
.container-fluid {
	padding: 0;
}

#map {
	height: 500px !important;
}

.well {
	background-image:none;
	border: none;
	background-color: #fff;
	box-shadow: none;
}

.small {
	width: 24% !important;
	display: inline-block !important;
	margin-right:4px; 
}

#chat_outer, #radio_outer, #buildings_outer {
	margin-top: -560px;
	width: 22%;
}

#chat_outer .panel-heading, #radio_outer .panel-heading, #buildings_outer .panel-heading {
	background-color: #e74c3c;
	color: #fff !important;
}
#radio_messages_important li {
	background-color: #c0392b;
	height: 27px;
	border-radius: 5px;
	color: #fff;
	-webkit-animation-name: blink;
	animation-name: blink;
	-webkit-animation-iteration-count: infinite;
	animation-iteration-count: infinite;
	-webkit-animation-timing-function: cubic-bezier(1, 0, 0, 1);
	animation-timing-function: cubic-bezier(1, 0, 0, 1);
	-webkit-animation-duration: 1s;
	animation-duration: 1s;
	text-transform: uppercase;
}

#missions ul, #buildings ul, #mission_list_krankentransporte ul, #mission_list_krankentransporte ul, #radio ul, #alliance_chat ul {
	padding-left: 10px !important;
}

.building_list_fms {
	border-radius: 10px;
}

.building_list_fms_5 { 
	border-radius: 5px;
}

#radio_messages .btn {
	border: 2px;
	background-color: #f5f5f5;
}

#radio_messages {
	font-size: 13px;
}

 #chat_outer li {
	font-size: 13px;
	line-height: 1.8;
}

