@-moz-document domain("www.leitstellenspiel.de") {
html, body {
    	max-width: 100%;
	overflow-x: hidden;
}

.mission_vehicle_state {
	position: relative;
	margin-top: -60px;
	margin-left: -20px;
	height: 33px !important;
	width: 30px !important;
}

.glyphicon {
	font-size:11px;
	line-height: 2;
}

.missionSideBarEntry {
	margin-bottom: 10px;
	margin-top: 10px;
	border: 10px;
	border-color: #34495e;
	border-bottom: solid;
	background-color: #f5f5f5;
	border-radius: 5px;
}

.panel-heading, .panel, .panel-body {
	border: none;
	box-shadow: none;
}

.panel-heading {
	background-image:none !important;
	/*background-color: #e74c3c !important;*/
	color: #34495e !important;
}


.panel{
	background-image: none !important;
	/*background-color: #bdc3c7 !important;*/
	color: #2c3e50 !important;
	border-radius: 10px;
	border-top: solid;
	border-color: #34495e !important;
}

.missionSideBarEntry .panel-body {
	background-color: #f5f5f5;
}

.map_position_mover { 
	color: #2c3e50 !important;
}

.navbar-default, .dropdown-menu {
	background-image: none !important;
	background-color: #e74c3c;
	border-radius: 0;
	border: none;
}

.btn, .progress, .alert {
	background-image: none !important;
	border: none;
	border-radius: 5px;
}

.progress-bar-striped, .progress-striped .progress-bar {
	background-size: 20px 20px;
	height: 15px;
}

.progress {
	background-color: #486683;
	height: 15px;
}

.small .progress {
	height: 6px;
}

#map_outer {
	margin-top: -20px;
}

 #missions_outer {
	margin-top: -10px;
}

.progress-bar-danger {
	background-color: #e74c3c;
}

.col-xs-11 {
	width: 100% !important;
	margin-top: -20px;
	position: relative;
}

/* Einsatzliste länge */
#missions-panel-body {
	height: 1000px !important;
}

#buildings_outer {
	margin-top: -550px;
	margin-left: 33.5%;
}

#chat_outer {
	margin-top: -560px;
}

#missions_outer {
	margin-left: -10px;
}
.container-fluid {
	padding: 0;
}

#map {
	height: 500px !important;
}

.well {
	background-image:none;
	border: none;
	background-color: #fff;
	box-shadow: none;
}

.small {
	width: 24% !important;
	display: inline-block !important;
	margin-right:4px; 
}

