css:
@-moz-document domain("gqueues.com") {
  /* ALL */
  
  * {
  	font-family: transit !important;
  }
    
  /* TOP BAR */
  
  #gqTopBar,
  .gq-search-container {
  	width: 180px;
  	bottom: 0px!important;
  	z-index: 0;
  	position: fixed;
  }
  
  
  /* TASK LIST */
  
  #gqQueueContent {
  	margin-left: 200px!important;
  }
  
  
  .gq-i-content {
  	border: none!important;
  	background: none;
  }
  /*# # #*/
  /* ROW */
  /*# # #*/
  
  /*fix identation gap*/
  
  tr.gq-i-notes-row > td:nth-child(1),
  tr.gq-i-files-row > td:nth-child(1),
  tr.gq-i-assign-row > td:nth-child(1),


  /*row buttons*/
  
  .gq-i-date-btn,
  .gq-i-assign-btn,
  .gq-i-files-btn,
  .gq-i-duplicate-btn,
  .gq-i-zoom-btn,
  .gq-i-check-btn,
  .gq-i-comments-btn,
  .gq-i-delete-btn,
  .gq-i-duplicate-btn,
  .gq-i-tags-btn,
  .gq-i-more-btn,
  .gq-i-reminder,
  .gq-i-repeat,
  .gq-i-order {
  	display: none!important;
  }
  
  .gq-i-sub-btn,
  .gq-i-sub-ctrls,
  .gq-i-sub-btn-open,
  .gq-i-sub-btn-open *,
  .gq-i-sub-btn div {
  	display: none;
  }
  
  .gq-i-spacer {
  	width: 130px!important;
  }
  
  .gq-i-selector {
  	top: 0!important;
  	background: #000!important;
  	opacity: 0.1;
  	left: 0%!important;
  	width: 100%!important;
  	height: 22px!important;
  	z-index: 0!important;
  	margin-top: 0rem!important;
  	font-size: 0!important;
  }
  
  .gq-i-notes-btn {
  	text-transform: uppercase;
  	position: absolute!important;
  	left: 0%!important;
  	height: 100%!important;
  	top: 0px!important;
  }
  
  .gq-i-notes,
  .gq-i-notes * {
  	border: 1px solid #aaa;
  }
  
  .gq-i-description {
  	font-weight: normal;
  }
  
  tr:hover [class$="btn"] {
  	display: none!important;
  }
  
  .gq-item-container[notesExist] tr:hover [class="gq-i-notes-btn"] {
  	opacity: 1!important;
  	display: block!important;
  }
  
  
  /* ####### date ####### */
  
  .gq-i-date-text {
  	text-transform: uppercase;
  	position: absolute!important;
  	left: 25px!important;
  }
  
  .overdue * {
  	color: lightcoral!important;
  }
  
  
  /* ############################################################# */
  /* Q U E U E   T R E E*/
  /* ### hide queue count ### */
  
  .gq-category-container .gq-queue-count {
  	display: none!important;
  }
  
  [class*="gq-smart-queue-sort-rows ui-sortable"] .gq-queue-count {
  	display: none!important;
  }
  /* ### tree entries ### */
  
  .gq-queue-panel-toggle,
  .gq-queue-panel-toggle collapsed {
  	background-image: none!important;
  }
  
  .gq-queue-handle,
  .gq-category-toggle {
  	display: none;
  }
  
  .gq-queue-panel-header * {
  	padding: 0!important;
  	margin: 0!important;
  	height: 1em!important;
  }
  
  .gq-category-name {
  	color: #aaa;
  	font-weight: bold;
  }
  /*hidden ##################################################*/
  
  #gqQueueContentSubhead,
  #gqNewFeaturesLink,
  #gqCalendarLink,
  #gqNotificationsBtn,
  #gqShareLink,
  #gqFeedbackLink,
  #gqLogoParent,
  #gqSettingsMenu span,
  #gqSearchBtn,
  .gq-category-icon,
  #gqQueueDescriptionToggle,
  #gqAccountMenu,
  #gqQuickAddBtn,
  #gqControlTopShadow,
  .gq-control-footer,
  .gq-category-toggle,
  .gq-smart-queue-separator,
  #gqTagQueuesPanel,
  #gqSharedQueuesPanel,
  .gq-i-parent-description,
  .gq-control-header,
  .gq-queue-panel-title,
  .gq-queue-assignments {
  	display: none!important;
  }
}
