css:
@-moz-document domain("codeanywhere.com") {
.CodeMirror-gutter.CodeMirror-lint-markers,.CodeMirror-gutter.CodeMirror-foldgutter {
    border-right: #2ab0b76b 1px solid;
}


.CodeMirror .CodeMirror-activeline .CodeMirror-linenumber {
    background-color: rgba(255, 0, 255, 1);
    border-right: purple 2px solid;
    color: #FFF;
    margin-right:-2px;
}
.CodeMirror-activeline {
    outline: rgba(0, 255, 161, 0.1) 1px solid !important;
    background-color: rgba(255, 0, 255, 0.15);
    box-shadow: rgba(0, 255, 161, 0.1) 0px 0px 3em, rgba(0, 255, 161, 0.6) 0px 0px 1em;
}

.CodeMirror .cm-comment {
    color: #606060;
    text-shadow: black 0 0 4px, black 0 0 2px, black 0 0 2px, black 0 0 2px, black 0 0 2px;
}

/* --------.cm-searching----------------------------------------------*/
.cm-searching {
    position: relative;
    border-left:#ffff0085 1px solid;
    border-right:#ffff0085 1px solid;
    border-bottom: 1px solid #ffe792;
}
.cm-searching:before {
    content: '';
    position: absolute;
    top: -50%;
    left: 40%;
    border: #ffffff00 0.4em solid;
    border-top-color: #fff20052;
}
.CodeMirror .cm-searching {
    background: #7d7a471f;
    box-shadow: rgba(246, 255, 0, .14) 0 0 1px 2px, rgba(246, 255, 0, .25) 0 0 2em;
    /*rgba(255, 0, 141, 0.1);*/
    /*     outline: yellow 1px solid; */
}
/* --------.cm-searching----------------------------------------------*/


/* --------.cm-matchhighlight----------------------------------------------*/
.CodeMirror .cm-matchhighlight {
    outline: rgba(112, 0, 255, .26) 4px solid;
    box-shadow: rgba(255, 0, 227, .41) 0 0 1em;
    border-bottom: 2px solid #FF0000;
    position: relative;
    text-shadow: #bf2394a1 -1px -1px 0px, #000 1px 1px 2px, #000 1px 1px 4px, #000 1px 1px 4px, #000 2px 2px 4px;
/*     margin-bottom:-2px; */
}
/* .cm-matchhighlight:after {
    content: '';
    position: absolute;
    bottom: -50%;
    left: 40%;
    border: #ffffff00 0.4em solid;
    border-bottom-color: #ff00ff52;
} */
/* --------.cm-matchhighlight----------------------------------------------*/


/* --------..cm-selection ----------------------------------------------*/
.cm-cm-overlay {
    background: rgba(0, 255, 141, .35) !important;
    outline: rgba(0, 255, 141, 0.1) 1px solid;

}


/* ::selection {
    color: #FFF !important;
    background: rgba(112, 0, 255, 0.45) !important;
	box-shadow: rgba(255, 0, 227, 0.8) 0 0 1em;
} */
.CodeMirror-focused .CodeMirror-selected,
.CodeMirror-line::selection,
.CodeMirror-line > span::selection,
.CodeMirror-line > span > span::selection {
    background: rgba(0, 255, 198, .21) !important;
    border-top: rgba(0, 255, 141, .5) 2px solid !important;
    box-shadow: rgba(0, 255, 141, 0) 0 0 4px;
    text-shadow: #000 1px 1px 2px, #000 1px 1px 4px, #000 1px 1px 4px, #000 2px 2px 4px, #fff -1px -1px 0px !important;
}

.CodeMirror .CodeMirror-selected {
    /* Selection of seached string */
    background-color: #00fee7b0;
    box-shadow: rgb(0, 229, 255) 0 0 1em;
}

::-webkit-selection,
::selection {
    color: #FFF !important;
    background: rgb(255, 115, 0) !important;
    box-shadow: rgba(132, 255, 0, .8) 0 0 1px 2px, rgb(0, 255, 247) 0 0 2em;
    outline: rgba(0, 255, 141, 0.1) 1px solid;
    text-shadow: #ff00b9ba -1px -1px 0px, #000 1px 1px 2px, #000 1px 1px 4px, #000 1px 1px 4px, #000 2px 2px 4px !important;
}
/* --------..cm-selection ----------------------------------------------*/
}
