css:
@-moz-document domain('zarabiam.com') {
    #forum_block {
    background: #202020 none repeat scroll 0 0;
    border-radius: 3px;
    box-shadow: 0 0 8px #000;
    margin-bottom: 15px;
    margin-right: 235px;
    padding: 14px;
    width: auto;
}

#messresult {
    background: #252525 none repeat scroll 0 0;
}

#panel #panel_header {
    background: firebrick none repeat scroll 0 0;
    border-radius: 2px;
    height: 26px;
    margin-bottom: 5px;
    width: 100%;
}

#panel_header_left {
    display: none;
}

#panel_header_right {
    display: none;
}

#posts {
    margin-top: -31px;
}

.alert_orange,.alert_black {
    display: none;
}

.forumbit_foot {
    display: none;
}

.menu .separate {
    background: none;
}

.menu ul {
    background: firebrick none repeat scroll 0 0;
}

.menu ul a {
    background: #801818 none repeat scroll 0 0;
}

.tborder {
    background: #151515 none repeat scroll 0 0;
    border: 1px solid #282828;
    border-collapse: unset;
    margin: 28px auto auto;
    padding: 2px;
    width: 100%;
}

.tcat {
    display: none;
}

.tfoot {
    background: firebrick none repeat scroll 0 0;
    border-radius: 1px;
    height: 26px;
}

.thead {
    background: firebrick none repeat scroll 0 0;
    border-bottom: 2px solid #151515;
    border-radius: 3px 3px 2px 2px;
    color: #ffffff;
    height: 30px;
    line-height: 25px;
    padding-left: 16px;
}

.trow1 {
    background: #252525 none repeat scroll 0 0;
    border: 0 none;
    padding: 13px 8px ;
}

.trow1, .trow2 {
    background: #252525 none repeat scroll 0 0;
    border-bottom: 1px solid #151515;
    border-top: 1px solid #303030;
}

.trow1_summary {
    background: #252525 none repeat scroll 0 0;
}

.trow2 {
    background: #252525 none repeat scroll 0 0;
    border: 0 none;
    padding: 13px 8px ;
}

.trow2_summary {
    background: #252525 none repeat scroll 0 0;
}

body {
    background: rgba(0, 0, 0, 0) url("http://i.imgur.com/FkBQTki.png") repeat scroll 0 0;
}
}
