css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lschronicles.net") {
  * { margin: 0; padding: 0; }
  
  body {
    background: url("http://image.noelshack.com/fichiers/2016/02/1452788932-helico.png") no-repeat;
    background-color: #57606B;
  }
  
  #brdmenu {
    width: 100%;
    background: #57606B;
    left: 0;
    top: 0;
    box-shadow: 0px 0px 5px black;
  }
  
  #brdmenu li {
  }
  
  #brdmenu li a {
    font-family: 'Lato', sans-serif;
    font-weight: 900;
    margin: 0;
    padding-top: 15px;
    padding-bottom: 15px;
    padding-left: 15px;
    color: white!important;
  }
  
  #brdmenu .isactive {
    background: #737D8B;
  }
  
  .rpitbarre {
    position: absolute;
    z-index: 1;
  }
  
  .punwrap {
    background: #B5B6B7!important;
  }
  
  .blocktable {
    background: #D5D9DA!important;
  }
  
  .box {
    background: #D5D9DA!important;
  }
  
  .blockpost, .postbody, .postfoot {
    background: #D5D9DA!important;
  }
  
  .blockpost h2 {
    background: #D5D9DA!important;
    display: block;
  }
  
  .blockpost h2 a, .postleft a {
    color: #57606B!important;
  }
  
  .postbody .usertitle {
    background: #D5D9DA!important;
    color: #57606B!important;
  }
  
  .blocktable h2 span {
    display: block;
    background: #CDD1D2;
    color: #798183;
    margin: -7px;
    padding: 5px;
  }

  .blocktable .box .inbox table th {
    background: #CDD1D2;
    color: #798183;
  }
  
  .postavatar {
    height: 180px!important;
    position: relative!important;
    top: -120px!important;
    left: 0!important;
  }
  
  .postavatar img {
    height: 180px!important;
    width: 120px!important;
  }
  
  .postfootright ul {
    margin-top: 20px!important;
  }
  
  @media screen and (min-width: 1800px) {
    .pun {
      max-width: 1430px!important;
      display: block;
    }
    
    .punwrap {
      width: 1400px!important;
      display: block;
    }
  }
}
