css:
@-moz-document url-prefix("http://www.youmath.it/") {
body {
    background-color: black;
}
div.container-bottom,
aside.right-module-position > div,
div#slider,
div.kfheadercontent,
#Kunena ul.kpagination > li.active,
#Kunena ul.kpagination > li > a,
#Kunena table.klist-actions-bottom,
#Kunena .kicon-button,
td.kcol-editor-label,
td.kcol-editor-field,
#Kunena fieldset,
div#cookieChoiceInfo,
#cawrapper ul.answers li.accepted_answer,
select,
option {
    background-color: #202020!important;
}
#cawrapper ul.data_table li div.question_title a {
    color: white;
}
a,
div#relateddata > ul > li > a {
    color: #0099cc;
}
body,
div.content-module > ul,
div.content-module > ul > li > a,
div.kfheadercontent,
select,
option,
#cawrapper .answerbox {
    color: white!important;
}
option:disabled {
    color: #f67300!important;
}
#Kunena ul.kpagination > li > a {
    border-color: white;
}
img.ltximg {
    filter: invert(1);
    -webkit-filter: invert(1);
}
#Kunena .klist-actions {
    width: 100%;
}
div.main_content {
    border-right-width: 0;
}
#Kunena .kicon-button span span {
    color: inherit!important
}
#Kunena div.kpathway {
    border: 0;
}
ins.adsbygoogle {
    border: 1px solid #f67300;
}
}

@-moz-document url-prefix("http://www.youmath.it/forum") {
input#subject,
input#subject:hover,
input#subject:focus,
input#helpbox,
textarea#kbbcode-message,
#Kunena ul.kpagination > li > a,
#Kunena ul.kpagination,
#Kunena fieldset > input.kinputbox {
    color: white!important;
}
input#subject,
input#subject:hover,
input#subject:focus,
input#helpbox {
    background-color: #202020!important;
}
input#subject.invalid {
    background-color: #FDD!important;
}
span.ktopic-date {
    color: #999
}
#Kunena td.kprofile-left,
span.online-no > span,
span.online-yes > span {
    background-color: #353535!important;
    border-color: #353535!important;
}
span.online-no > span {
    color: #D0D0D0!important;
}
#Kunena tr.krow2-stickymsg,
#Kunena tr.krow1-stickymsg {
    background-color: #383838!important;
}
#Kunena div.kblock div.kbody table {
    border-right: 1px solid #E5E5E5;
}
#Kunena div.kblock div.kheader {
    border: 1px solid #E5E5E5;
    border-top: none;
}
}
