css:
@-moz-document domain("isis.tu-berlin.de"), domain("shibboleth.tubit.tu-berlin.de") {
body,
#rightheader,
#isis-section,
#home,
#head,
#nav-meta,
#login,
div.section {
    color: #bbb !important;
    background-color: #222 !important;
    border: 0px;
    text-shadow: 0 1px 0 rgba(0, 0, 0, 0) !important;
}
div#branding,
#main fieldset p,
#nav-meta ul,
.block,
.header,
.title,
.section,
.section .content,
.navmeta {
    background-color: #333 !important;
    background-image: none;
    color: #ccc;
    text-shadow: 0;
}
.block,
.header,
.title,
li.section {
    border: 0 !important;
    border-radius: 10px !important;
}
input[type="submit"] {
    color: #b8b8b8 !important;
    background: #383838 !important;
    background-image: linear-gradient(to bottom, #000, #181818);
    border-color: #111 !important;
    text-shadow: 0 0 0 rgba(0, 0, 0, 0)!important;
}
#page-footer {
    border-top: 1px solid #555;
    margin-top: 16px;
}
.navbar-fixed-top .navbar-inner,
.navbar-static-top .navbar-inner {
    background-color: #222 !important;
    background-image: none;
}
img {
    -webkit-filter: invert(87.5%) !important;
}
img.texrender {
    -webkit-filter: invert(100%) !important;
}
#wrapper {
    border: 0px !important;
}
#search-box,
.search-box,
.navbar .nav.pull-right {
    background-color: #333;
    width: 219px;
    border-left: 1px solid #555;
}
.navbar .nav.pull-right {
    border-bottom-left-radius: 5px;
}
.block a,
a {
    color: #d22;
}
.block a:hover,
a:hover {
    color: #637ab5;
    text-decoration: none;
}
.title h2 {
    border: 0px;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    color: #ccc !important;
    background-color: #444;
    text-shadow: 0 0 0 rgba(0, 0, 0, 0) !important;
}
.breadcrumb a {
    color: #bbb;
}
.coursebox {
    border: 1px solid #444;
}
.navbar .nav>li>a {
    text-shadow: 0 0 0 #777 !important;
}
abbr[title],
abbr[data-original-title] {
    border-bottom: 0;
}
input#searchform_search {
    margin: 5px !important;
}
/* scroll bar */

::-webkit-scrollbar {
    max-width: 10px !important;
    max-height: 10px !important;
    background: #181818 !important;
}
::-webkit-scrollbar-track,
::-webkit-scrollbar-corner {
    background: #181818 !important;
}
::-webkit-scrollbar-thumb {
    border-radius: 5px !important;
    background: rgba(80, 80, 80, .6) !important;
}
::-webkit-scrollbar-thumb:hover {
    background: rgba(96, 96, 96, .6) !important;
}
span {
    color: #999 !important;
    background: transparent !important;
    text-shadow: 0 0 0 rgba(0, 0, 0, 0) !important;
}
li,
p {
    color: #ccc !important;
    background: transparent !important;
}
h3 {
    color: #ddd !important;
}
}
