css:
@-moz-document url-prefix("http://old.anotalk.hu/")
{
  body, #backBtn {
    font-family: "HelveticaNeue-Light", sans-serif !important;
  }

  .message-last {
    margin-top: 4px;
    margin-bottom: 4px;
  }

  .message-last:first-of-type {
    margin: auto;
    padding-top: 6px;
  }
  
  .message-last:first-of-type > .message-text {
    text-align: center;
    margin-left: 0px;
    margin-right: 0px;
    padding-left: 20px;
    padding-right: 20px;
}

  .message-last:last-of-type {
    margin-bottom: 10px;
  }

  .message-sent > .message-text {
    padding-left: 12px;
  }

  .message-received > .message-text {
    padding-right: 12px;
  }

  #findp > h3:after {
    content: ".";
  }

  #backBtn {
    cursor: pointer;
  }

  #backBtn > span:last-of-type {
    display: none;
  }
  
  #gender b, #pgender b {
    font-weight: normal;
  }

  .page.no-toolbar {
    background-color: #333;
    background-image: none !important;
  }

  [data-page="panel"] .navbar:after, [data-page="index"] .navbar:after {
    display: none;
  }

  .button.button-secondary {
    border: 0px;
    color: white;
  }

  .button.button-secondary.active, .button.button-secondary.button-fill,
  .button.button-secondary.active-state {
    background-color: #777;
    color: white;
  }

  .blog-box li .blog-list .text .title {
    color: black;
  }

  .item-content-icon::after {
    color: black;
  }

  .blog-box li .blog-list .text .title a {
    color: black;
  }

  .list-block ul:before {
    display: none;
  }

  .list-block .item-link.active-state,
  html:not(.watch-active-state) .list-block .item-link:active {
    background-color: #999;
  }

  .blog-box li:after, .comments-box li:after {
    display: none;
  }

  .user-banner .ava-box {
    display: none;
  }

  .user-banner.big .options-2 a, .user-banner.big .options a {
    border-bottom: 0px;
    border-right: 0px;
  }

  h3 {
    font-family: "HelveticaNeue-Light", sans-serif !important;
  }

  .spinner {
    display: none;
  }

  .navbar {
    background-color: #666;
  }

  .center.sliding {
    display: none;
  }

  #panelLeft {
    background-color: #666;
  }

  .panel .list-block .list-group ul:before {
    display: none;
  }

  .panel .list-block .list-group ul li:after {
    display: none;
  }

  .list-block:active {
    background-color: #777;
  }

  .list-block ul:after {
    display: none;
  }

  .message {
    margin-left: -20px;
  }

  .message.message-sent .message-text {
    background-color: #333;
  }

  .message .message-text b:before {
    content: "Generált kérdés: ";
  }

  .message .message-text b {
    color: #777 !important;
    font-weight: normal;
  }
  
  .message-last:first-of-type .message-text {
    background-color: white !important;
  }

  .message-name {
    display: none;
  }

  .message-avatar {
     display: none;
  }

  #findpartner {
    color: #000 !important;
    background-color: #fff !important;
    border-width: 0px !important;
    margin-top: -42px;
    padding-bottom: 32px;
  }

  #findpartner:active {
    background-color: #fff !important;
  }

  #asdbtn {
    color: black;
  }

  .navbar:after {
    background: none;
  }

  .toolbar:before {
    background: none;
  }

  .messagebar textarea {
    border: 0px;
  }

  .adsbygoogle {
    display: /*[[ads]]*/ !important;
  }

  .adsbygoogle:last-of-type {
    margin-top: -19px;
    margin-left: -4px;
  }
}
