css:
@-moz-document domain("hyunsdojo.com") {
#header {
	background-image: none;
    background: black;
    color: #FF0000!important;
}
.menu_btn {
    color: #ED8A59!important;
}

#footer {
    background:none;
}

/*removes shadows*/

* {
    -webkit-box-shadow: none !important;
    box-shadow: none!important;
    border: white!important;
    text-shadow: none!important;
    }


/* Header, footers etc */
.forum_cat, .view_forum_top, #front_menu .front_menu_a.label, .more_info h3, .ptl_cat, dd input, .submit-buttons input, .box_header, .box_footer, .right_cat, .right_cat .button, .post_info, .box_header, #m_head, #m_foot, #ptl_menu .front_menu_a.label, .ptl_title, .new_post_footer, table#abbc3posttable, #abbc3posttable > tbody > tr > td, #swf_viewport_bottom, #ptl_footer, .box_top, .u_comments_box, .previewPosted, .front_submit, .front_menu_a:hover:not(.front_submit):not(.label), .view_forum_separator, .view_forum_bottom, button, .ptl_submit, #ptl_menu a:hover:not(.ptl_submit):not(.label), .recommended, .spoilwrapper, .quotetitle, .attachtitle, #settings_btn, .dropdown, .dropdownlist, .button, #swf_comments_box, .paginate, .paginate_btn, #u_show_list_top, #pm_footer, #ptl_list_top, .codetitle, .photo_view_wrap, #off_viewport_bottom,.top_bread a, .comment_text,#profile_name{
	background: black!important;
    color: white!important;
}
 #profile_name span {
              color: white!important;
}
/* Main body color */
#profile_top_footer, .status_update, .activity_right, .user_content_avatar, .u_comments_top, .u_com_btn, .front_menu_a:not(.front_submit), #profile_avatar, .right_row, .forum_row, .forum_row_right, .post_avatar, .post_right, .new_post_footer, .new_post_body, .tablebg, .row1, .row2, #ptl_menu, .notify_box, .right_data, #notifications_box, #messages_drop, .m_msg, #user_box_drop, .drop_item, #ptl_main, #ptl_menu a:not(.ptl_submit), .list_msg_inner, .new_post_wrap, #swf_desc_box, .quick_news, .top_stats, #front_menu, .uplbg, a.post_button, .top_foot_btn, #swf_comments_top, .com_btn, #ptl_cont, #u_s_container, .list_msg, #viewfolder, #pm_menu a, #pm_menu, .ucp_list_msg, #ucp_list_top, .cp_cont, .pm_list_msg, .friend_box, .panel, a.notify_box, .right_data, #notifications_box, #messages_drop, .m_msg, #user_box_drop, .drop_item, .clicked, .m_clicked, .clicked div, .m_clicked div, .sm_clicked, .sm_clicked div, .logo:hover, .photo_view_sidebar, .signin_box1, .recommended_sub{
    background: black!important;
    color: white!important;
}

.forum_wrap,#status_box,.post_right{
    border-style: solid!important;
    border-width: 3px!important;
    border-color: #FFFFFF!important;
    background: black;
}
.post_right{
    padding: 13px;
}

.post_avatar, .user_content_avatar img{
    animation: bobble 2s infinite;
}

@keyframes bobble {
    0% {
        transform: scaleY(1);
        animation-timing-function: ease-in;
    }
    50% {
        transform: scaleY(1.1);
        animation-timing-function: ease-out;
    }
    100% {
        transform: scaleY(1);
        animation-timing-function: ease-out;
    }
}
.forum_row .forum_row_name, .forum_row a, .drop_item strong{
color: white!important
}
/*following written by m_x*/

.photo_view_image {
    max-width: 100% !important;
    width: auto !important;
    max-height: 100vh !important;
    }

body {
    background: url('http://www.thebackalleys.com/dump/files/4545/974hP1_gridtale.png') 0 0;
    background-position: center;
    }

.front_submit,
.ptl_submit,
.post_error,
.ptl_up_btn {
    background: linear-gradient(to bottom, rgb(125, 30, 30) 0%, rgb(224, 143, 199) 100%);
    }

#profile_top_footer {
    background: linear-gradient(to bottom, #FFF 0%, #FFF 65px);
    }

.forum_cat,
.ptl_title,
#ptl_menu .front_menu_a.label,
#front_menu .front_menu_a.label,
#chat_tabs .chat_tab .chat_new_box .chat_box_title,
.button,
.right_cat,
.usr_cat,
input[type=submit],
button,
#chat_tabs .chat_tab,
.more_info h3,
.ptl_cat,
dd input,
.submit-buttons input,
.box_header,
.box_footer {
    background: linear-gradient(to bottom, #000 0%, #000 100%);
    }

.paginate,
.paginate_btn {
    background: linear-gradient(to bottom, rgb(212, 203, 190) 0%, rgb(236, 227, 219) 100%)!important;
    }


.quotetitle,
.attachtitle,
.codetitle {
    border-color: #BFBB9C;
    background-color: #BFBB9C;
    }

.post_avatar, .user_content_avatar, .user_content_avatar_full, .staff_avatar, .user_icon, .auth_av, .f_sm, .comment_av, #profile_avatar{
  filter: grayscale(100%) brightness(80%) sepia(100%) hue-rotate(60deg) saturate(0%) contrast(10) !important;
  -webkit-filter: grayscale(100%) brightness(80%) sepia(100%) hue-rotate(60deg) saturate(0%) contrast(10) !important;
}
}
