css:
@-moz-document domain("ist-webadvisor.abtech.edu") {

body {
    background: #B2B2B2;
}
html,
body,
div,
h1,
h2,
h3,
h4,
h5,
h6,
table,
td,
tr,
p {
    font-family: Helvetica, Arial, sans-serif;
}
BODY,
DIV,
TABLE,
TD,
P {
    font-size: .9rem;
}
a,
a:link,
a:visited,
.WBST31 .menu a {
    text-decoration: none;
    color: #2A5DB0;
}
a:hover,
.WBST31 .menu a:hover {
    text-decoration: underline;
    color: #79222d;
}
.toolbar {
    margin-top: -25px;
    padding: 15px;
}
.toolbar .label {
    color: #FFEECC;
    font-family: inherit;
    font-size: 1.2rem;
    font-weight: bold;
    line-height: 1.5;
}
.toolbar a:link, 
.toolbar a:visited {
    text-decoration: none;
    color: #ffeecc;
}
.toolbar a:hover,
.toolbar a:active  {
    text-decoration: underline;
    color: white;
}
#acctLogin span,
#acctLogout span {
padding: 4px 8px;
background: rgb(245, 207, 138);
color: maroon;
}
#acctLogin span:hover,
#acctLogout span:hover {
background: rgba(0,0,0,.25);
color: white;
}
#acctLogout span{
   background: rgba(255,0,0,.25);
  color:white;
}
#acctContact,
#sysHelp,
#acctHelp, 
#acctMain,
#sysMain,
#sysLogin,
#acctLogin,
#acctLogout,
#sysContact,
#sysLogout,
#acctConstituency,
#sysConstituency {
width:auto;
padding: 4px 8px;
display:inline-block;
}
#pageHeader {
    background-color: #E4E4E4;
}
#webPage {
    position: relative;
    max-width: 1440px;
    min-width: 780px;
    margin: 0 auto;
    background: #ffffff;
}
.menu:after,
#mainBody:after {
    visibility: hidden;
    display: block;
    font-size: 0;
    content: "   ";
    clear: both;
    height: 0;
}
.menu ul {
    list-style-image: none;
    margin: inherit;
    padding: inherit;
    font-size: inherit;
}

.menu .left {
    float: left;
    width: 45%;
}
.menu .right {
    float: right;
    width: 45%;
    padding-top: 0px;
}
.menu .submenu {
    font-size: 1.2rem;
margin-top:20px;
}
.menu .left li a, 
.menu .right li a,
.menu li,
.menu li a{
font-size: 1.1rem;
line-height: 1.5rem;
padding-left: 2px;
margin: 5px 0;
}
input,
select,
input option,
select option {
    font-size: 1rem;
    padding: 5px;
}
input,
select,
table select,
.WBST31 table tr td select,
.WBST31 table tr td.oddrow select,
.WBST31 table tr td.evenrow select,
.WBST31 table tr td.oddrow input,
.WBST31 table tr td.evenrow input {
background-color: #FAF8EF;
}
input:active,
select:active,
input:focus,
select:focus,
table select:active,
table select:focus,
table input:active,
table input:focus,
.WBST31 table tr td select:active,
.WBST31 table tr td select:focus,
.WBST31 table tr td.oddrow select:active,
.WBST31 table tr td.evenrow select:active,
.WBST31 table tr td.oddrow select:focus,
.WBST31 table tr td.evenrow select:focus,
.WBST31 table tr td input:active,
.WBST31 table tr td input:focus,
.WBST31 table tr td.oddrow input:active,
.WBST31 table tr td.evenrow input:active,
.WBST31 table tr td.oddrow input:focus,
.WBST31 table tr td.evenrow input:focus {
    background-color: #FFF9DE;
}
.WBDF {
    background-color: #E4E4E4;
    background-image: none;
}
#mainMenu a {
    margin-top: 5px;
    height: auto;
    font-size: 1rem;
    border: none;
}
#mainMenu a:hover {
    background: #660033;
    color: white;
    border: none;
}
#mainMenu a.WBST31_Bars,
#mainMenu a.WBCE_Bars,
#mainMenu a.WBFC31_Bars,
#mainMenu a.WBEM_Bars,
#mainMenu a.WBAD_Bars {
    border: none;
    background-color: #F5CF8A;
}
#mainMenu a.WBST31_Bars:hover,
#mainMenu a.WBCE_Bars:hover,
#mainMenu a.WBFC31_Bars:hover,
#mainMenu a.WBEM_Bars:hover,
#mainMenu a.WBAD_Bars:hover {
    border: none;
    background-color: #660033;
}
.WBST31 .menu .submenu a,
.WBST31 .menu .submenu a:hover {
    color: black;
    cursor: default;
}
input.shortButton,
.WBFC31 .submit input,
.WBDF .submit input,
.WBCE .submit input,
.WBST31 .submit input,
.WBFC31 .submit input.buttonPress {
    background-color: #F5CF8A;
    border-color: none;
    color: maroon;
    text-align: center;
    padding: 10px;
    border: none;
}

input.shortButton:hover,
.WBFC31 .submit input:hover,
.WBDF .submit input:hover,
.WBCE .submit input:hover,
.WBST31 .submit input:hover,
.WBFC31 .submit input.buttonPress:hover {
    cursor: pointer;
    background-color: #ffeecc;
color:maroon;
}
table {
    /*margin:   10px   auto;*/
}
table th,
table tr,
table td {
    padding: 4px;
}
table th {
    padding: 0px 6px;
    font-size: .9rem;
}
table p {
    font-size: .9rem;
}
td.SEC_SHORT_TITLE {
font-weight:bold;
}
td.control.left {
padding-right:20px;
}

#headerBanner .custom {
    padding: 20px;
}
.customLinks {
    padding: 10px 0px 0px 0px;
    text-align: right;
}
#pageFooter .customLinks a {
    font-size: .8rem;
    color: #FFEECC;
}
#pageFooter .customLinks a:hover {
    color: white;
}
#mainForm {
    float: right;
    width: 30%;
    margin: 2%;
}
#mainMenu {
    width: 100%;
    padding: 0px;
}
#mainBody .custom {
    float: left;
    width: 60%;
    padding: 2%;
}
#mainBody .customText,
#bodyForm .customText,
.customText p {
    color: #333333;
    font-size: .9rem;
}
.submit {
    padding: 5px;
    text-align: center;
    margin: 5px 0;
}
#helpPage {
    font-family: arial, helvetica, sans-serif;
    position: relative;
    max-width: 1280px;
    min-width: 320px;
    margin: 0 auto;
}
#helpPage #bodyForm {
    margin-left: 10px;
    margin-right: 10px;
    margin-top: 10px;
    padding: 20px;
    background-color: white;
}
.WBCE table tr td.oddrow {
    background-color: #efefef;
    border: 1px solid black;
}
select option[selected] {
    color: green;
}
select option[value="A"] {
    color: red;
}
select option[value="L"] {
    background: yellow;
    color: red;
}
select option[value="P"] {
    color: green;
}
form[name="WebWiz"] {
    padding: 20px;
    font-size: 1rem;
    max-width: 1400px;
    margin: 0 auto;
    background: white;
}
form[name="WebWiz"] p {
    font-size: .8rem;
    line-height: 1;
}
form[name="WebWiz"] a,
form[name="WebWiz"] a:visited {
    text-decoration: none;
    color: #2A5DB0;
    font-size: inherit;
    background: transparent;
}
form[name="WebWiz"] a:hover {
    text-decoration: underline;
    color: #79222d;
    background: transparent;
}
form[name="WebWiz"] table {
    background: white;
}
form[name="WebWiz"] table th,
form[name="WebWiz"] table td {
    font-size: 12px;
    font-family: arial;
}
form[name="WebWiz"] table select {
    padding: 2px;
}
div#bodyConstituency {
    font-size: 2rem;
}

.WBFC31 table select,
.WBST31 table select {
    height:30px;
}

.WBFC31 table tr td.oddrow,
.WBST31 table tr td.oddrow {
    background-color: #E4E4E4;
    border: 1px solid black;
}
form [name="WebWiz"] table select{
width:100%;
}

#cot_tl_fixed {
z-index:999;
}


}
