css:
@-moz-document url("http://community.wikia.com/wiki/Special:Chat") {
/* Chat Code */
 
#WikiaPage {
border:1px solid #3d454d;
background-color:rgba(0, 0, 0, 0.75);
}

body.ChatWindow {
background: url(http://vignette2.wikia.nocookie.net/kallenbearrp/images/3/3c/Seamless-black-leather-texture.jpg/revision/latest?cb=20160113083514);
background-repeat: repeat ;
}

body.ChatWindow .you .avatar {
    border: 1px solid lightslategray; border-radius: 2px 6px; box-shadow: 0 0 6px lightslategray;
}

body.ChatWindow .avatar {
    border: 1px solid darkviolet; border-radius: 6px 2px; box-shadow: 0 0 6px darkviolet;
}

.ChatWindow a { color: royalblue;
}

.ChatWindow a:visited { color: #be00bf;
}

.ChatWindow #Chat_211 {
    border: solid #3d454d;
    border-width: 0 1px 1px 0;
}

.ChatWindow #ChatHeader, .ChatWindow #WikiaPage {
    border: 1px solid #3d454d;
}

.ChatWindow #ChatHeader { 
    background: rgba(10, 10, 10, 0.80) url(http://vignette3.wikia.nocookie.net/kallenbearrp/images/e/e4/Corrupted_stucco_wall_texture_seamless.jpg/revision/latest?cb=20160113142901);
background-repeat: repeat ;
}

.ChatWindow .WikiaPage {
background: rgba(0,0,0,.8) !important;
}

.ChatWindow .you {
background: transparent !important;
border-right: 3px ridge lightslategray;
border-left: 3px ridge lightslategray;
border-style: double;
}
 
.ChatHeader .User {
border-left:1px solid #3d454d;
}

.ChatHeader .User img {
border: 1px solid white; border-radius: 2px 6px; box-shadow: 0 0 4px white;
}

.ChatHeader .User .username {
font-weight: bold;
color: white;
background: transparent;
}

.ChatWindow #ChatHeader .username {
    color: white;
}
 
.Chat {
color: #d5d4d4;
border-right:1px solid #3d454d;
border-bottom:1px solid #3d454d;
background: url(http://vignette4.wikia.nocookie.net/kallenbearrp/images/5/51/Googletile.png/revision/latest?cb=20160113083017);
background-repeat: repeat;
}

.Chat .inline-alert {
    color: #767676;
    font-size: 9px;
    height: auto;
    line-height: normal;
    font-weight: normal;
}

.Chat .time {
    color: darkviolet;
}

.Chat .username {
    color: darkviolet;
    font-weight: bold; 
    text-shadow:0px 0px 2px black;
}
 
.ChatWindow .ChatHeader {
background-color:rgba(0, 0, 0, 0.75);
border:1px solid #3d454d;
color: lightslategray;
}
 
.Chat .avatar {
border:1px solid #3d454d;
}

.ChatWindow .me-message {
    color: #767676;
}
 
.ChatWindow .UserStatsMenu { 
background-color:rgba(0, 0, 0, 0.45); 
box-shadow: inset 0 0 0px 0px #000000; -webkit-box-shadow: inset 0 0 0px 0px #000000;
border:1px solid #3d454d; 
border-radius:3px; 
}
 
.ChatWindow .UserStatsMenu .info { 
background:transparent;
color:#d5d4d4;
}
 
.ChatWindow .UserStatsMenu .actions ul li:hover { 
background:transparent; 
}

.message.ping { color: orange !important;
}

#Write .message { 
    background: transparent;
    border: solid 1px #3d454d;  
}
#Write .message textarea { 
    color: white; 
}
 
#Rail h1.private {
    color: lightslategray;
}

textarea#pings {
    margin-top: -1px;
    background: rgba(113, 5, 109, 0.44);
    border: 1px solid lightslategray;
    color: #ccc;
}

.User img {
border:1px solid #3d454d;
}

.User .username {
    color: darkviolet;
    font-weight: bold; 
    text-shadow:0px 0px 2px black;
}
 
.UserStatsMenu .info img {
border: 1px solid #3d454d;
}

.UserStatsMenu .info ul .username {
color: darkviolet;
}

.UserStatsMenu .actions ul li .icon { 
background-position:0;
background-image:url('http://slot1.images.wikia.nocookie.net/__cb1452521916/common/extensions/wikia/Chat2/images/sprite-Chat.png');
opacity:100; 
}

.UserStatsMenu .actions ul li:hover {
    background-color: rgba(113, 5, 109, 0.44) !important;
}
 
.UserStatsMenu .actions ul li:hover .icon { 
opacity:100; 
}
 
.UserStatsMenu .actions .separator {
border-top: white 1px solid;
margin: 30px !important;
}

.User.away {
color: #767676;
}

.wordmark {
    color: orange;
}

.Write {
background: url(http://vignette3.wikia.nocookie.net/kallenbearrp/images/d/db/Metal_tile7.png/revision/latest?cb=20160113142838);
background-repeat: repeat ;
}

.Write [name="message"] {
color: #d5d4d4;
}
 
.Write img {
border:1px solid #3d454d;
}
 
.Write .message {
    border: 1px solid rgba(61, 69, 77, 0.97);
}

.Write img, .ChatHeader img {
border: transparent; border-radius: 2px 6px;
}

.Write img, .ChatHeader .User img { 
border: 1px solid white; border-radius: 2px 6px; box-shadow: 0 0 4px white;
}
 
.Rail {
color: lightslategray;
background: url(http://vignette3.wikia.nocookie.net/kallenbearrp/images/5/50/Seamless-leather-texture7.jpg/revision/latest?cb=20160113142812);
background-repeat: repeat ;
}

.Rail .User {
    background: transparent;
    margin: 0 0 2px 0px;
    border: 1px transparent;
    padding-top: 4px;
}
 
.Rail .public .chevron { 
border-color:lightslategray transparent transparent transparent; 
}
 
.Rail .public .chevron.closed { 
border-color:transparent transparent transparent orange; 
}
 
.Rail .private {
border-top: 1px solid #3d454d;
border-bottom: 1px solid #3d454d;
background-color:transparent; 
background-image:-webkit-gradient(linear,0% 0%,0% 0%,color-stop(0,transparent),color-stop(0,transparent));
}
 
.Rail .selected { 
background:transparent; 
box-shadow: inset 0 0 0px 0px #000000; -webkit-box-shadow: inset 0 0 0px 0px #000000; 
}

.Rail .User.selected, .Rail .selected {
    background: rgba(0, 0, 0, 0.7);
}

.Rail .User:hover, .Rail .User.selected:hover, .Rail .selected:hover {
    background: rgba(113, 5, 109, 0.44);
    box-shadow: none;
    border-color: transparent;
}

.Rail .User img {
    border: 1px solid darkviolet; border-radius: 6px 2px;  box-shadow: 0 0 6px darkviolet;
    width: 28px;
    height: 28px;
    left: 4px !important;
    top: 2px !important;
}

.UserStatsMenu .info img {
    border: 1px solid darkviolet; border-radius: 6px 2px;
}

.UserStatsMenu .actions ul li
.label {
color: royalblue;
cursor: pointer;
margin: 0;
padding: 5px 10px;
line-height: 16px;
white-space: nowrap;
}

.you {
    color: lightslategray;
    font-weight: bold; 
    text-shadow:0px 0px 2px black;
}

/* transparent chat buttons (chat hacks buttons) */

a.wikia-button, .wikia-single-button a, .wikia-menu-button, input[type="submit"], input[type="reset"], input[type="button"], .button, button {
    background-color: rgba(0, 0, 0, 0.03);
    background-image: -moz-linear-gradient(top,#1a1a1a 35%,#000 65%);
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(35%,rgba(26, 26, 26, 0.05)),color-stop(65%,rgba(0, 0, 0, 0.04)));
    background-image: -o-linear-gradient(top,#1a1a1a 35%,#000 65%);
    background-image: -ms-linear-gradient(top,#1a1a1a 35%,#000 65%);
    border: 1px solid rgba(0, 0, 0, 0.05);
    border-top-color: rgba(0, 0, 0, 0.0470588);
    border-top-style: solid;
    border-top-width: 1px;
    color: rgba(65, 105, 225, 0.89);
}

/* SCROLLBAR */

::-webkit-scrollbar {
  width: 3px;
  height: 3px;
}

::-webkit-scrollbar-thumb:vertical, ::-webkit-scrollbar-thumb:horizontal {
  background-color: white;
    border-radius: 1px;
    border:1px solid dimgray;
}

::-webkit-scrollbar-thumb:hover {
    background: royalblue !important;
}

::-webkit-scrollbar-thumb:active {
    background: orange !important;
}

::-webkit-scrollbar-track:vertical, ::-webkit-scrollbar-track:horizontal {
  background-color: transparent;
}

::-webkit-scrollbar-corner {
  background-color: dimgray;
}
 
::-webkit-scrollbar-button:start:decrement,
::-webkit-scrollbar-button:end:increment {
  display: none;
}
 
/* END OF CODES */


}
