css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("buy.fashionvictime.fr") {
  
  body,
  .columns-container,
  .header-container:not(.isStuck),
  #tmsearch .btn.button-search
  {
    background: #FFFFFF !important;
  }
  
  header,
  .header_bottom,
  header .nav{
    background: transparent !important;
  }
  
  .header-container:not(.isStuck) .header_bottom {
    margin-top:84px !important;
  }
  
  .header-container:not(.isStuck) {
    height: 250px !important;
  }
  
  .isStuck {
    background: rgba(51,51,51, 0.97); 
    border:none !important;
  }
  
  #htmlcontent_top ul li li .wrapper,
  .top_menu > ul > li > a,
  #htmlcontent_top ul li li a:hover
  {
    border:none !important;
  }
  
  .top_menu > ul > li > a.sf-with-ul:before {
    display: none;
  }
  
  body, a {
    color:#333333;
  }
  
  .index #page {
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#ffffff+0,f7f7f7+20,f7f7f7+75,ffffff+90 */
background: #ffffff; /* Old browsers */
background: -moz-linear-gradient(top,  #ffffff 0%, #f7f7f7 20%, #f7f7f7 75%, #ffffff 90%); /* FF3.6-15 */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(20%,#f7f7f7), color-stop(75%,#f7f7f7), color-stop(90%,#ffffff)); /* Chrome4-9,Safari4-5 */
background: -webkit-linear-gradient(top,  #ffffff 0%,#f7f7f7 20%,#f7f7f7 75%,#ffffff 90%); /* Chrome10-25,Safari5.1-6 */
background: -o-linear-gradient(top,  #ffffff 0%,#f7f7f7 20%,#f7f7f7 75%,#ffffff 90%); /* Opera 11.10-11.50 */
background: -ms-linear-gradient(top,  #ffffff 0%,#f7f7f7 20%,#f7f7f7 75%,#ffffff 90%); /* IE10 preview */
background: linear-gradient(to bottom,  #ffffff 0%,#f7f7f7 20%,#f7f7f7 75%,#ffffff 90%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */

  }
  
  .columns-container {
    background: transparent !important;
  }
  
  .breadcrumb,
  .btn.btn-default{
    background: #333;
  }
  
  .breadcrumb a {
    background:transparent;
    color:#FFFFFF;
  }
  
  .product-information .product-info-tabs > li:hover, .product-information .product-info-tabs > li.active {
    background: transparent;
    font-weight: 600;
  }
  
  .top_menu > ul > li > a,
  .footer-container #footer h4,
  .page-heading span.heading-counter,
  .block .title_block, .block h4,
  .block .title_block, .block h4 a,
  .pb-right-column .nav a,
  #contact-link a.headerBlogLink
  #tmsearch .form-control,
  div.selector,
  .product-information .product-info-tabs > li,
  .product-information .product-info-tabs > li:hover, .product-information .product-info-tabs > li.active,
  .btn.social-sharing i,
  .pb-right-column .product-info-line > p label,
  #quantity_wanted_p label
  {
    color:#333333 !important;
  }
  
  .cdareinsurance_block,
  .btn.btn-default{
    border-color: #333333 !important;
  }
  
  .pb-right-column .nav a,
  #contact-link a.headerBlogLink
  #tmsearch .form-control
  {
    text-transform: uppercase !important;
    font-weight: 500 !important;
    letter-spacing: 0.04em;
    font-size:11px !important;
  }
  
  .isStuck .top_menu > ul > li > a {
    color:#FFFFFF !important;
  }
  
  #index #columns {
    padding-top:0;
  }
  
  ul.product_list.grid > li {
    margin-bottom: 8px;
  }
  
  .breadcrumb {
    margin-bottom:80px;
  }
  
  .block .title_block, .block h4 {
    background: transparent;
    border-bottom: 4px solid #333;
    padding:0 0 6px 0;
    text-transform: none;
    font-weight: 500;
    font-size: 21px;
    letter-spacing: 0.03em;
  }
  
  .page-heading {
    text-align: center;
    font-size:28px;
    font-weight: 300;
    padding-bottom:15px;
    border-bottom: 4px solid #333 !important;
    position: relative;
  }
  
  .page-heading span.heading-counter {
    float:none;
    position: absolute;
    right: 0;
    bottom:9px;
  }
  
  .top-pagination-content {
    display:none;
  }
  
  .content_sortPagiBar .sortPagiBar {
    border:none;
    margin-bottom:30px;
  }
  
  div.selector {
    background: transparent;
    border:none;
  }
  
  #layered_block_left .layered_subtitle,
  .content_sortPagiBar .sortPagiBar label, .content_sortPagiBar .sortPagiBar select,
  .content_sortPagiBar .display li.display-title, .content_sortPagiBar .display_m li.display-title
  {
    font-size: 14px;
  }
  
  .btn.btn-default {
    color:#FFFFFF;
  }
  
  .isStuck .top_menu > ul > li > a::after {
    background:#FFFFFF;
  }
  
  .header-container:not(.isStuck) .top_menu > ul > li > a::after {
    height:4px;
  }
  
  .product-information .product-info-tabs > li {
    font-weight: 300;
  }
  
  .product-information .tab-content > div {
    padding-left:30px;
  }
  
  .pb-right-column .product-info-line > p span.editable {
    background: #333;
    color:#FFFFFF;
  }
  
  .btn.social-sharing {
    width:36px;
    height:36px;
    overflow: hidden;
    border-radius: 18px;
    background:transparent !important;
    border:1px solid #333 !important;
    margin:0 2px;
  }
  
  #left_column section .block_content,
  #left_column section .block_content a,
  #left_column section .block_content dt,
  #left_column section .block_content li,
  #left_column section .block_content p {
    font-size: 11px !important;
  }
  
  #layered_block_left .nomargin + .nomargin {
    padding:0;
  }
  
  section h3 {
    margin-top:60px !important;
  }
  
  .gallery-thumb-list,
  .quick-view {
    display: none !important;
  }
  
  .page-product-box {
    border-bottom:none;
  }
  
  #our_price_display {
    font-weight: 300;
  }

  .our_price_display {
	margin:30px 0 20px;
  }
  
  .box-cart-bottom {
	margin-top:30px;
  }

  .pb-right-column .product-info-line {
    margin-bottom:0;
  }
  
  .pb-right-column .container {
    padding-right:30px !important;
    padding-left: 30px !important;
  }
  
  .top_menu > ul {
    margin-left:-15px;
  }
  
  .header-container:not(.isStuck) .header_logo_a,
  .header-container:not(.isStuck) #img_logo {
    width:346px !important;
    height:230px !important;
  }
  
  .header-container:not(.isStuck) .header_logo_a {
    margin-left:35px;
  }
  
  #product_reference {
    display: none;
  }
  
  .pb-right-column h1 {
    font: 400 24px/28px "BentonModDisp", sans-serif;
    padding-bottom: 30px;
  }
  
  .box-info-product .ajax_add_to_cart_product_button:before,
  #wishlist_button::before, #wishlist_button_nopop::before {
    display: none;
  }
  
  .btn.btn-circle {
    width:60px;
    height:60px;
    font-size:20px !important;
  }
  
  .btn-circle * {
    font-size:25px !important;
  }
  
  .shopping_cart > a:first-child::before { 
    display: none;
  }
  
  #contact-link a,
  #header-login .header_user_info.current a,
  .shopping_cart > a:first-child,
  #tmsearch #tm_search_query
  {
    padding: 10px;
  }
  
  .shopping_cart > a:first-child > b {
    display: inline !important;
    font-family: "GothamRounded", sans-serif;
    font-weight: inherit;
    font-size: inherit;
    color:inherit;
    padding-right: 0;
  }
  .shopping_cart > a:first-child .ajax_cart_quantity {
    background: transparent;
    font-size: inherit;
    font-weight: inherit;
    font-family: inherit;
    color:inherit;
    min-width: 0;
    padding:0;
    height:auto;
    display: inline !important;
  }
  
  .shopping_cart > a:first-child > b,
  .shopping_cart > a:first-child .ajax_cart_quantity {
    line-height: 18px;
  }
  
  .shopping_cart > a:first-child .ajax_cart_quantity:before {
    content:"(";
  }
  
  .shopping_cart > a:first-child .ajax_cart_quantity:after {
    content:")";
  }
  
  #header-login .header_user_info.current a::before {
    display: none;
  }
  
  #contact-link {
    margin:0;
  }
  
  #tmsearch {
    margin:0;
    position: absolute;
    right:0;
    top:70px;
  }
  
  #tmsearch .btn.button-search {
    width:40px;
    height:40px;
    border-radius: 20px;
    background:#333 !important;
  }
  
  #tmsearch .btn.button-search:before {
    color:#FFF;
  }
  
  #tmsearch .btn.button-search:hover::before {
    color:#FFFFFF;
  }
  
  #tmsearch #tm_search_query {
    background: #333 !important;
    color:#FFF !important;
    margin-top:4px;
    padding-left:0;
    padding-right:0;
    position: absolute;
    right:0px;
    border-radius: 20px;
    overflow: hidden;
    width:40px;
    transition: all 0.5s ease;
  }
  
  #tmsearch:hover #tm_search_query {
    width:auto;
    padding-left:20px;
    padding-right:100px;
  }
  
}
