@-moz-document domain("inoreader.com") {
body,
.open_sans {
	font-family: "Roboto", sans-serif !important;
}
*::-webkit-selection {
	background: #b4e7f8;
}
h1,
h2,
h3,
h4,
h5 {
	display: none
}
.article_content {
	font-family: "Lora", serif !important;
	line-height: 1.45em !important;
	text-align: justify;
}
.article_content a, .article_content a.underlink.bluelink {
text-decoration: none !important;
color: #1A1A1A;
background: linear-gradient(#fff,#fff),linear-gradient(#fff,#fff),linear-gradient(#FF3530,#FF3530);
background-size: .05em 1px,.05em 1px,1px 1px;
background-repeat: no-repeat,no-repeat,repeat-x;
text-shadow: .03em 0 #fff,-.03em 0 #fff,0 .03em #fff,0 -.03em #fff,.06em 0 #fff,-.06em 0 #fff,.09em 0 #fff,-.09em 0 #fff,.12em 0 #fff,-.12em 0 #fff,.15em 0 #fff,-.15em 0 #fff;
background-position: 0 95%,100% 95%,0 95%
}
.article_content a:hover {
	color: #FF3530;
	transition: color .30s;
	background: 0 0;
}
.article_content img,
.article_content iframe {
	margin: 0 auto !important;
	max-width: 95% !important;
	margin-right: auto;
	margin-left: auto;
	text-align: center;
	border-width: 1px;
}
.article_card .article_title,
#article_dialog .article_title {
	padding-right: 0px;
}
.article_title {
	font-family: "Roboto", sans-serif;
}
.article_sub_title {
	height: 40px;
}
blockquote {
	background: #f9f9f9;
	border-left: 7px solid #ccc;
	margin: 1.5em 10px;
	padding: 0.5em 10px;
	quotes: "\201C""\201D""\2018""\2019";
}
blockquote:before {
	color: #ccc;
	content: open-quote;
	line-height: 0.1em;
	margin-right: 0.25em;
	vertical-align: -0.4em;
}
blockquote p {
	display: inline;
}
.article_content img {
	border-color: #e1e1e1;
	border-style: solid;
	border-width: 1px;
	box-sizing: border-box;
	height: auto !important;
	max-width: 90% !important;
	padding: 0.15em;
	display: block !important;
	margin-left: auto !important;
	margin-right: auto !important;
	margin-bottom: 2.5em !important;
	overflow: hidden !important;
}
#sb_rp_upgrade,
#sb_rp_section_options,
.ad_footer_remove,
.inno_toolbar_button_upgrade,
#sinner_container {
	display: none !important;
	;
	right: 0px !important;
}
#sb_rp_gear {
	right: 0px!important;
}
#sb_rp_notifications {
	right: calc(114px - 74px)!important;
}
#sb_rp_tools {
	right: calc(152px - 74px)!important;
}
#wraper {
	min-width: calc(930px - 120px - 74px)!important;
}
#subscriptions_nav {
	padding-top: 0 !important;
}
#tree_pane span.unread_cnt,
#tree_pane span.icon-checkmark {
	color: #D24231 !important;
}
#tree_pane #tree {
	padding-bottom: 30px !important;
}
#reader_pane.reader_pane_sinner {
	padding-right: 0px;
}
#reader_pane.reader_pane_view_style_0 > #consent_https_info {
	padding-top: 6px !important;
	padding-bottom: 6px !important;
}
#reader_pane.reader_pane_view_style_0 > div.ar {
	margin-top: 2px !important;
	margin-bottom: 2px !important;
}
#reader_pane.reader_pane_view_style_0 > div.ar div.article_header > div {
	padding-top: 6px !important;
	padding-bottom: 6px !important;
}
#reader_pane.reader_pane_view_style_0 > div.ar div.article_header > div.article_favicon {
	margin-right: 8px !important;
}
#reader_pane.reader_pane_view_style_0 > div.ar div.article_header >div.arrow_div {
	top: 1px !important;
}
#reader_pane.reader_pane_view_style_0 > div.ar.article_expanded > div.article_header {
	margin-left: 2px !important;
	padding-left: 8px !important;
}
#reader_pane.reader_pane_view_style_0 > div.ar:not(.article_expanded):hover,
#reader_pane.reader_pane_view_style_0 > div.ar.article_expanded > div.article_header:hover {
	box-shadow: 0 0 2px 2px #2E9AFE !important;
}
div.article_full_contents {
	padding-top: 5px !important;
}
div.article_full_contents > div.article_title {
	margin-top: 0 !important;
	margin-bottom: 0 !important;
}
div.article_full_contents > div.article_sub_title {
	margin-top: 8px !important;
}
div.article_full_contents > div.article_content {
	padding-top: 8px !important;
	padding-bottom: 8px !important;
}
div.article_full_contents > div.article_content > p:first-child {
	margin-top: 0 !important;
}
div.article_full_contents > div.article_content > p:last-child {
	margin-bottom: 0 !important;
}
div.article_full_contents > div.article_content a:visited {
	color: #554FCA !important;
}
}
