css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nu.nl"), domain("www.nu.nl") {
  html {
    margin-top: 0px ! important;
}

body,
#wrapper,
.block-content,
.block-wrapper,
.horizontal,
.zone,
.ring,
.nav-item,
.nav-wrapper,
.block,
.webring,
#container,
#header,
.media-footer,
.socialbar,
.socialbar div,
.socialbar ul,
.socialbar ul li {
    background: #000 !important;
    border-color: #333 !important;
    color: #eee
}
a {
    color: #d9d9d9 !important
}
.nav-main {
    color: #84B3AC !important
}
.divider,
.dotted,
.vertical,
.block-content,
.block-wrapper,
.slogan,
.zone,
.column-content,
.tab,
div,
span,
p {
    border-color: #000 !important
}
.advertoriallist,
.widget-wrapper,
.tabtrigger,
li.branded,
.molecule-feedlist-footer,
.newsletter,
.section-advertorial,
#block-342457,
#block-332867,
#block-332871,
#block-332883,
#block-332881,
#block-288907,
#block-328423,
.adblocker {
    display: none !important
}
.block-content,
.item-excerpt,
.title h1 {
    color: #d9d9d9 !important
}
.trackevent,
a.nav-main {
    background: none !important;
    background-image: none !important
}
.list-overlay,
.list-overlay a,
.list-overlay span,
.block-content ul li {
    background: #0C0C0C !important;
    border-color: #333 !important;
    color: #eee
}
span.caption {
    color: #ccc !important
}
ul.social-buttons li a span.counter {
    background: #333 !important
}
ul.social-badges li a i {
    text-shadow: none !important;
    color: #333
}
input,
button {
    border: none !important;
    color: #333;
    background: #333 !important
}
input {
    color: #eee
}
span.excerpt,
span.caption {
    color: #aaa !important
}
}
