css:
/* i really want this to be global */
@-moz-document domain('mokum.place'){
.box-header, .box-header-empty, .box-header-timeline, .box-header-friends, .box-header-groups {
    border: none;
    direction:rtl;
    font-family:Tahoma;
        font-size: 12px;
}


body {
    background-color:#f1f1f1;
    font-family:Tahoma;
        font-size: 12px;
  }


.content .box-body {
    border:solid 1px #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
    font-family:Tahoma;
        font-size: 12px;
}

.sidebar .box-body {
       background-color: #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
    font-family:Tahoma;
        font-size: 12px;
}
 
div.box-header-timeline {
    font-family:Tahoma;
    font-size: 12px;
}

li.previous a, li.next a {
    font-family:Tahoma;
        font-size: 12px;
    background-color: #d1d1d1;
    color: #000000;
}

.timeline-post-container .post-body .text{
        font-family:Tahoma;
        font-size: 12px;
}
.comments{
    direction:rtl;
            font-family:Tahoma;
        font-size: 12px;
text-align:left;
}

.content a {
    font-family:Tahoma;
        font-size: 12px;
}

.header .title{
    width:260px;
    background-image:url('https://mokum.place/system/attachments/000/030/508/mokum-thumb-bfc55ff7e2a1d194564a8ec7d184eb69998e5516.png');
    background-repeat:no-repeat;
    text-indent: -9999px;
    white-space: nowrap;
    margin-top:20px;
}

.header .search{
    margin-top:30px;
}
}
