css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neues-deutschland.de"){
  
#PUB .Wrapper-Ads {
    background: #2f2f2f;
}
#PUB .Wrapper {
    background: #393939;
}
#PUB .Navi.Main li a {
  color: #009098;
}
#PUB .Navi.Meta li.Login a, #PUB .Register .Login a {
  color: #009098;
}
#PUB div.Main .Content a, .Article-Special .Content a, .cke_editable a {
  color: #009098;
}
#PUB .Box {
    background: #393939;
}
#PUB a {
  color: #009098;
}
#PUB .Specials .Article-Specials .Article-Specials-Wrap {
    background: #333 none repeat scroll 0% 0%;
    border-bottom: 1px solid #333;
    border-left: 10px solid #333;
    border-top: 1px solid #333;
    margin-left: 20px;
    width: 370px;
}
#PUB .Specials .Box.Reiter .Content {
    background: #393939;
}
#PUB .Box .Desc li a, #PUB .Box .Content li a {
    color: #009098;
}
#PUB .Tools {
    background: #393939;
}
#PUB .Tools-Bottom {
    background: #393939;
}
#PUB .Main .Commenting ul li.Comment p {
    background: #ccc;
}
#PUB .Main .Commenting .Comment-Bottom {
    background: #ccc;
}
#PUB .Main .Commenting ul li.Comment {
    background: #ccc;
    color: #202020;
}
#PUB div.Main .Content h4, .Article-Special .Content h4, .cke_editable h4, .cke_panel_list h4 {
  color: #d15397;
}
#PUB .Box h4 a {
  color: #009098;
}
#PUB div.Main .Listing .Article-Teaser li a strong, #PUB div.Main .Listing .Listing-Stopper li a strong  {
    color: #009098;
    }
#PUB div.Main .Listing .Listing-Stopper {
    background: #393939;
}
#PUB .Table-of-contents {
    background: #343434;
}
#PUB p.Footer {
    background: #393939;
}
#PUB .Box .Desc p a {
  color: #009098;
}

}
