css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.thegamesmachine.it") {
 html{
   background-color: #273556 !important;
    }
  
  #aktia-ads{
    display:none;
    
  }
  #tgm-adv-main{
    display:none !important;
  }
  
  #aktia-ads skin{
   display:none; 
   }
  
  body {
    margin-top: 0px ;
    width: 90% ;
  }
  
  .body_wrapper{
   background-color: #5C7099 !important;
  }
  
  .postbody, .threadlist, div.bodywrapper{
   background-color: #BFD2F3 !important; 
   border: 0px !important;
  }
  
  .threadbit, .nonsticky, .discussionrow, .forumrow{
   background-color:  #BFD2F3 !important;  
    border-style: none none none !important;
    border: 1px;
  }
  
  .postbit, .postbitlegacy, .eventbit{
    border: 0px !important;  
  }  
  
  .threadbit a{
      color: #555555 !important;
  }
  
  #pagetitle a {
    color: #3B37E3 !important;
  }
  
  .threadlist a, .discussionrow a, .forumrow  a, .threadtitle a, .label a{
    color:#555555 !important;
    
  }
  
  .postcontent a{
    color: #0000ff !important;
  }
  
  a.usercp_folder-left, li.inactive a{
    
    color: #000000 !important;
  }
  
  body a{ color:#dddddd !important;}
  
  .pagination span a{
    color: #273556 !important;
  }
  
  .formcontrols .blockrow {
    background-color: #5C7099 !important;
    border: 0px;
    padding: 0px;
  }
  
  .navtabs ul.floatcontainer {
    background-color: #273556 !important;
  }
  
  .column-left, column-right{
    background-color: #2E3539 !important;
  }

  .thread_info_block, .blockbody {
    border: 0px !important;
}
  .doc_header,  .navtabs{background-color: #5C7099 !important;}
  
  .navlinks {
    background-color:#273556 !important;
    border: 0px;
  }
  
 .toolsmenu{ border:0px; margin:0px; background-color: #5C7099 !important;}
 .postdetails{background-color: #A5B5D9 !important;}
 .postbitlegacy{background-color: #A5B5D9 !important;}
 .userinfo{background-color: #A5B5D9 !important;}
 .postbitlegacy{background-color: #A5B5D9 !important;}
 .eventbit{background-color: #A5B5D9 !important;}
 .eventdetail{background-color: #A5B5D9 !important;}
 .textcontrols{background-color: #A5B5D9 !important; border-top:0px !important;}
  
  .posthead,  .blockbody{background-color: #5C7099 !important;}
  .postfoot{background-color: #A5B5D9 !important; border:0px !important;}  
  
  .footer, .prefooter{
    display: none !important;
    
  }
  
  .title icon{
    display: none !important;
   
  }
  
  textarea.ncode_imageresizer_warning {
    font-size: 8px;
    font-family: Verdana,Arial,Helvetica,sans-serif;
    background-image: none !important;
    background-color: #BFD2F3!important;
    color: #4F4300!important;
    border: 0px;
    padding-right: 0px!important;
    margin-top: 0px;
    height: 27px !important;
    cursor: pointer;
    resize: none;
    overflow: hidden;
    border-radius: 0px;
    text-align: right;
}

}
