css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("die.net") {
  html, body { background-color: #000C17 !important; }
  * { color: #777 !important; }
  a:link, a:visited, a:focus { color: #00ACAF !important; }
  
  #bg { max-width: 4000px !important; width: 100% !important; }
  
  body { background-image: none !important; } /* main page & left nav bar */
  #news { background-color: #000C47 !important; } /* main page */
  #logo, #menu { background-color: #000C27 !important; } /* left nav bar */
  input[type="text"] { background: transparent none !important; border: 1px solid #00ACAF !important; }
  
  h1 { color: #00ACAF !important; }
  h2 { color: #000CC7 !important; }
  
  dl dd *, dl dd { color: red !important; }
  
  b { color: green !important; }
  i { color: orange !important; }
}

/* google search results */
@-moz-document regexp('.*rurl=http%3A%2F%2Fwww\\.die\\.net.*') {
  * { background-color: transparent !important; color: #777 !important; border: 1px solid transparent !important; }
  body { background-color: transparent !important; }
  a:link, a:visited, a:focus { color: #00ACAF !important; }
  .gsc-table-result:hover { background-color: #000C47 !important; }
}

/* fricken ads */
@-moz-document regexp('https://cse\\.google\\.com/cse\\?q.*') {
  * { background-color: transparent !important; color: #777 !important; }
}
