css:
@-moz-document domain("twitter.com") {   
  
/* bg and text color */
body, html, .ProfilePage .AppContent {
color: #cecece !important;  
/*background: url() !important;*/
background: #14151f !important;
}
  
/* content bg color */  
.module .flex-module, .DashboardProfileCard-content, .Trends, .tweet, .global-nav .search-input, .DashboardProfileCard-avatarLink, .content-main .expansion-container > .original-tweet-container, .content-header .header-inner, .stream-item-activity-notification .stream-item-activity-line, .stream-item-activity-notification .activity-supplement, .stream-end-inner, .ProfileHeaderCard, .ProfileSidebar--withLeftAlignment, .WhoToFollow.is-visible, .ProfileHeading-toggle, .ProfileNav-list, .ProfileCanopy-navBar, .ProfileNav-item--userActions, .ProfileAvatar, .Grid-cell {
background: #1d1e2a !important;
}  
  
/* content & hover */
.tweet:hover, .top-timeline-tweetbox .tweet-user, .new-tweets-bar, .new-tweets-bar:hover, .top-timeline-tweetbox .timeline-tweet-box .tweet-form.condensed .tweet-box, .global-nav-inner, div.profilecard-content, .dashboard-profilecard-stats, .DMActivity-container, .DMNotice, .DMActivity-header, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox, div.notie, .content-main .expansion-container > li:last-child .view-more-container, .global-nav .pull-right .dropdown-menu {
background: #1a1b26 !important;  
}  
  
/* text color */
.stream-item .content, .Trends .context-trend-item .trend-item-stats, .Trends .trend-location, .Footer-item, .metadata.social-context, h3, input, .with-icn, .QuoteTweet-screenname, .QuoteTweet-text, .DMActivity-title, .DMEmptyState-header, .ProfileHeading-toggleItem.is-active, .ProfileHeaderCard-locationText, .ProfileHeaderCard-joinDateText, .ProfileNav-item.is-active .ProfileNav-value, .ProfileNav-stat, .metadata, .global-nav .form-search, .bird-topbar-etched {
color: #cecece !important;
}  
  
/* link color */ 
a, .TweetTextSize .twitter-atreply b, s, b, .account-group .fullname, .import-prompt p > span, .account-group .username, .DashboardProfileCard-statLabel, .Trends .context-trend-item .trend-item-context, .DMNotice-message, strong, .ProfileNav-item.is-active .ProfileNav-label, .ProfileNav-item.is-active:hover .ProfileNav-label, .ProfileNav-label, .ProfileUserList-permalink, a.u-textUserColor, .ProfileNav-item.is-active .ProfileNav-label, .btn-link, .dropdown-menu .dropdown-link {
color: #3B94D9 !important;
}  
  
/* date color */
.time {
    color: #3B94D9 !important;
}  

  /* remove borders */
.tweet, .module .flex-module, .wtf-module .import-prompt .cta,  .top-timeline-tweetbox .timeline-tweet-box, .new-tweets-bar, .top-timeline-tweetbox .timeline-tweet-box .tweet-form.condensed .tweet-box, .global-nav .search-input, .QuoteTweet, .Trends, .module, .stream-item:not(.no-header-background-module), .content-main .expansion-container > .original-tweet-container, .DMActivity-header, .content-header .header-inner, .content-header, .stream-item-activity-notification, .stream-end, .ProfileHeading-content, .ProfileCanopy-navBar, .ProfileAvatar, .WhoToFollow.is-visible, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox, .conversations-enabled .inline-reply-caret, .conversations-enabled .inline-reply-caret .caret-inner, .tweet .stats, .stream-item .stats .avatar-row a:first-child, .dropdown-caret .caret-inner, .dropdown-menu li {
  border: none !important;
}  
  
/* buttons */
.follow-btn.small-follow-btn, .follow-btn, .DashboardProfileCard-avatarImage {
background: #1a1b26 !important;  
color: #3B94D9 !important;  
border: 1px solid #14151f !important;
}    
  
/* tweet and header buttons */
.u-textUserColorHover, .ProfileTweet-action--retweet .ProfileTweet-actionButton, .ProfileTweet-actionButton, .ProfileTweet-actionCount, .nav > li .Icon, .nav > li > a .text, .ProfileHeaderCard-location .Icon, .ProfileHeaderCard-url .Icon, .PhotoRail-heading .Icon, .ProfileHeaderCard-joinDate .Icon, .global-nav .search-icon .Icon, .ProfileTweet-action--retweet .ProfileTweet-actionButton:hover, .ProfileTweet-action--retweet .ProfileTweet-actionButton:focus, .ProfileTweet-action--retweet .ProfileTweet-actionCount:hover, .ProfileTweet-action--retweet .ProfileTweet-actionCount:focus, .tweet.retweeted .ProfileTweet-action--retweet .Icon--retweet, .tweet.retweeted .ProfileTweet-action--retweet .ProfileTweet-actionButtonUndo {
color: #345d7c !important;    
}  
  
.dismiss {
color: #345d7c !important;    
}  
   
/* autohide modules (who to follow, copyright, trends and profilecard */
  
.Trends, .module .flex-module:last-child, .module .flex-module, .DashboardProfileCard, .dashboard {
  /*display: none !important;*/
opacity: 0 !important;
}    
.Trends:hover, .module .flex-module:last-child:hover, .module .flex-module:hover, .DashboardProfileCard:hover, .dashboard:hover {
opacity: 0.9 !important;
}      
 
.nav > li > a .text {
opacity: 0 !important;
}  
  
.btn {
background: transparent !important;  
}  
  
#global-nav-home, .people, .dm-nav {
max-width: 50px !important;  
}  
  
/* hide tweet button in header */
#global-new-tweet-button, .global-nav .search-icon .Icon {
display: none !important;  
}  
  
.stream-item-header .avatar {
opacity: 0.7 !important;  
}  
  
.global-nav .search-input {
width: 500px !important;
margin-left: -570px !important;
opacity: 0 !important;  
}
.global-nav .search-input:hover {
opacity: 0.9 !important;  
}  
  
.tweet, .home-tweet-box, .global-nav {
box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.3) !important;
}  
   
}
