css:
@namespace html url("http://www.w3.org/1999/xhtml");

 
 
/* !!!!!!!!!!!!!!!!!!! favicon fix !!!!!!!!!!!!!!!!!!! */
#page-proxy-favicon {
		-moz-image-region: rect(0, 16px, 16px, 0) !important;
	}

/* !!!!!!!!! favicon !!!!!!!!! */
.bookmark-item[label="about:config"],
#main-window[stylish-url="about:config"] #page-proxy-favicon,
.tabbrowser-tab[label="about:config"] .tab-icon-image ,
#identity-box[label="about:config"] 
{
list-style-image: url("data:image/png;base64,") !important;
}

 
 
/* !!!!!!!!!!!! ВСЯКИ КРАСОТЫ !!!!!!!!!!!! */



treechildren {
margin-left: 0px !important; /* отступ слева для всех столбцов */
/* padding: 0px 2px 2px 0px !important; */ 

}
.tree-rows {
/* background: #fff !important; */ /* ??? */
}





/* зебра */ 
/* влияет в т.ч. на окно БИБЛИОТЕКА и SIDEBAR */
treechildren::-moz-tree-row(even) {
 background-image: -moz-linear-gradient(top, rgba(255, 255, 255, .08),
 rgba(255, 255, 255, .08)) !important; 
 background-image: linear-gradient(to bottom, rgba(255, 255, 255, .08),
 rgba(255, 255, 255, .08)) !important; 
}
treechildren::-moz-tree-row(odd) {
 background-image: -moz-linear-gradient(top, rgba(0, 0, 0, .05),
 rgba(0, 0, 0, .05)) !important; 
 background-image: linear-gradient(to bottom, rgba(0, 0, 0, .05),
 rgba(0, 0, 0, .05)) !important; 
}



/* бегунок */ 
/* влияет в т.ч. на окно БИБЛИОТЕКА и SIDEBAR */
/* также влияет на выпад.списки в search bar и в about:addons */
treechildren::-moz-tree-row(hover) {
 background-color: #6C0B71 !important; /* #30c */
}
treechildren::-moz-tree-cell-text(hover) {
 color: yellow !important;
}




/* выделенная строка */
treechildren::-moz-tree-row(selected),
treechildren::-moz-tree-row(selected,hover){
background-color: #30c !important; /* лучше как бегунок */
 /* background-color: -moz-menuhover !important; */
}
treechildren::-moz-tree-cell-text(selected),
treechildren::-moz-tree-cell-text(selected, open),
treechildren::-moz-tree-cell-text(selected,hover){
color: yellow !important; /* лучше как бегунок */
 /* color: -moz-menuhovertext !important; */
}








/* заголовки столбцов */
/* вся строка заголовка */
#configTree > treecols:nth-child(1) {
 background-color: #666 !important;
}

/* по которому сортировка */
treecol[sortDirection="descending"] ,
treecol[sortDirection="ascending"] {
 font-weight: bold!important; 
 color: #006000 !important;
 background: linear-gradient(to bottom, 
 rgba(250,250,250,1) 0%, 
 rgba(155,175,140,1) 100%) !important;
} 

/* treecol !! влияет на вып. списки */
/* treecol:not([class="autocomplete-treecol"]) - убрать treecol из вып. списка */
treecol:not([class="autocomplete-treecol"]) , 
treecolpicker:not([class="autocomplete-treecol"]) {
 -moz-appearance: none !important;
 border: 0px solid;
 border-bottom: 0px solid ; /* 0 ff38 */
 -moz-border-bottom-colors: rgb(0,0,0) !important;
 background: linear-gradient(to bottom, 
 rgba(250,250,250,1) 0%,
 rgba(160,160,160,1) 100%) ;
 color: #000000 ;
 font-size: 13px;
 height: 21px /* !important */ ;
 padding: 0px 4px;
}

#configTree .tree-splitter, 
#configTree splitter {
 /* -moz-appearance: none !important; */
 color: transparent !important;
 max-width: 0px !important; 
 width: 0px !important; 
 border: 0px solid #666666 !important; 
 background-color: transparent !important;
 background: transparent !important;
 -moz-binding: none !important;
 min-height: 21px !important;
 height: 21px !important;

/*     visibility:  hidden  !important;          */
}

#configTree treecol {
 border-right: 0px solid #666666 !important;
 border-left: 0px solid #666666 !important;
 margin-right: 1px !important;

} 
/* Имя настройки */
#configTree #prefCol{
 border-left: 0px !important;
 border-right: 0px !important; 
} 
/* справа квадратик настроек */
#configTree treecolpicker {
 border-left: 0px solid #666666 !important; 
 border-right: 0px !important;
 margin-right: 0px !important;
} 


treecol:hover , 
treecolpicker:hover {
 color: #000090 !important;
 background: linear-gradient(to bottom, 
 rgba(255,255,255,1.0) 0%,
 rgba(130,150,180,1.0) 100%) !important;
}
treecol:hover:active , 
treecolpicker:active {
 background: linear-gradient(to bottom, 
 rgba(255,255,255,1.0) 0%,
 rgba(130,180,130,1.0) 100%) !important;
}

treechildren::-moz-tree-column(insertbefore) {
 -moz-border-start: 1px solid rgba(215,79,37,1) !important;
}
treechildren::-moz-tree-column(insertafter) {
 -moz-border-end: 1px solid rgba(215,79,37,1) !important;
} 


 
 
 
 
/* !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!! */

@-moz-document url-prefix("about:config") {

/* всё окно */
#config vbox {
 background-color: #dadae5 !important ; /* rgba(90, 90, 125, 0.4205) */
}


/* заголовок + список */
#configTree {
 -moz-appearance:none !important;
 border: 3px solid #c00 !important; /* рамочка вокруг списка */
 margin: 0px 0px 1px 0px !important; 
 /* сверху справа снизу слева */
 padding-left: 0px; /* сдвигает вместе с заголовками */
}
 
/* заголовки столбцов */
treecol:not([class="autocomplete-treecol"]) , 
treecolpicker:not([class="autocomplete-treecol"]) {
/* color: #000000 !important ; */
 font-size: 14px;
 height: 24px;
}
 
/* background списка */
#config #configTreeBody {

 /* !!!!! ШРИФТ !!!!! */
 font-size: 14px !important; 

/* background position >>>> center bottom right */
/* 
 background: rgba(255, 255, 255, 0.02) url("data:image/jpeg;base64," ) !important; 
 background-position: center !important ; 
 background-size: 60% !important;
*/
 
/* ! в желтом треугольнике */
 /* background: rgba(255, 255, 255, 1.0) url("http://i164.photobucket.com/albums/u32/IlanF/warning.png"); */
 background:white url("data:image/png;base64,") !important;
 background-position: bottom right !important ; 
 background-size: 30% !important;

/* 
 background-color: #CCD9EA !important;
 background-image: -moz-linear-gradient(top, rgb(231,237,246), rgba(231,237,246,0) 156px), 
 url("chrome://mozapps/skin/extensions/background-texture.png") !important;
*/
 
/* opacity: 1.0; */
 background-color: #FFFFFF !important;
 background-repeat: no-repeat !important;

}

/* СТРОКИ */ 

#configTreeBody::-moz-tree-cell-text(), 
treechildren::-moz-tree-cell-text(),
treechildren::-moz-tree-row(even), 
treechildren::-moz-tree-row(odd), 
treechildren::-moz-tree-row(selected) ,
treechildren::-moz-tree-row {
 padding-top: 0px !important;
 padding-bottom: 0px !important;
 min-height: 19px !important;
}
 
/* ИЗМЕНЁННЫЕ СТРОКИ */
#configTreeBody::-moz-tree-cell-text(user) {
/* color: #79232F ; */
}

 
 
 

/* блок строки поиск=фильтр */
#filterRow{
 margin: 2px 0 !important;
}
#filterRow > label{
 font-size:14px !Important;
 color: #000000 !important; 
 font-weight: normal !important; 
}
 
/* строка поиск/фильтр about:config */
#textbox.compact {
 -moz-appearance:none !important;
 line-height: 1.5 !important;
 color: #3B6BC8 !important;
 font-size: 14px !important; 
 font-weight: bold !important; /* Bold */
 font-style: normal !important; /* Italic normal */
/* background: #fff !important; */
}
#textbox {
 -moz-appearance: none !important;
 padding: 1px 6px !important; 
 border: 1px solid #958E79 !important;
 border-radius: 0px !important;
 background-color: #F4F4f9 !important;
 background-clip: padding-box !important;
 -moz-transition-property: background-color, border-color, box-shadow !important;
 -moz-transition-duration: 1200ms !important;
 -moz-transition-timing-function: ease !important;
}
#textbox:hover, #textbox:focus {
 background-color: #FFFFFF !important;
 border-color: hsla(210,15%,25%, 0.5) !important;
 box-shadow: 0 0 3px 0px hsla(210,15%,25%,.81) !important;
 -moz-transition-property: background-color, border-color, box-shadow !important;
 -moz-transition-duration: 200ms !important;
 -moz-transition-timing-function: ease !important;
}
/* крестик очистки */
.textbox-search-clear:hover, 
.textbox-search-clear:focus {
 filter: hue-rotate(160deg);
}

 
 
} /*------------ @-moz-document url-prefix("about:config") ------------------ */
