css:
@-moz-document url-prefix("https://www.thegamesmachine.it/forum") {
html {
    background: #273556;
}
body {
    width: 95%;
    min-width: 1000px;
}
.body_wrapper {
    background: #c9dbff;
}
.toolsmenu {
    background: #869BBF;
    color: #000;
}
.toolsmenu a {
    color: #000 !important;
}

.forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow, 
.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow,
.forumrow + .childsubforum .forumrow, .forumhead + .childforum .L2:first-child .L2 .forumrow, .forumhead + .childforum .L2 .L2:first-child .forumrow
{
    background: #bfd2f3;
}

#threadlist * {
    font-size: 13px !important;
}
.threadbit .nonsticky,
.threadbit .discussionrow,
.threadbit .sticky {
    background: #bfd2f3;
    border-color: #000;
}
.threadbit,
.threadbit a {
    color: #000;
}
.postbitlegacy * {
    border-color: #000 !important;
}
.postbitlegacy .postrow,
.eventbit .eventrow,
.postbitdeleted .postrow,
.postbitignored .postrow {
    font-size: 13px;
}
.postbit .posthead,
.postbitlegacy .posthead,
.eventbit .eventhead {
    background: #5C7099;
    font-size: 12px;
    color: #000;
}
.postbitlegacy .posthead .time {
    color: #000;
    font-size: 12px;
}
.postbitlegacy .userinfo {
    font-size: 11px;
    background: #a5b5d9;
}
.postbitlegacy .postbody,
.eventbit .eventdetails .eventbody {
    background: #bfd2f3;
}
.postbitlegacy .postdetails,
.eventbit .eventdetails {
    background: #a5b5d9;
}
.postbit .postfoot .textcontrols,
.postbitlegacy .postfoot .textcontrols,
.eventbit .eventfoot .eventcontrols {
    background: #5C7099;
}
.postbitlegacy .postfoot .textcontrols a {
    color: #FFF;
}
.postbitlegacy .postfoot .textcontrols a:hover {
    color: #CCC;
}

.threadbit .threadstatus { 
    background-image: url("data:image/png;base64,"); 
}

.threadbit.dot.hot .threadstatus {
	background-image: url("data:image/png;base64,"); 
}

.threadbit.hot.lock .threadstatus { 
    background-image: url("data:image/png;base64,"); 
}

.threadbit.hot .threadstatus { 
    background-image: url("data:image/png;base64,"); 
}

.new .threadstatus { 
    background-image: url("data:image/png;base64,"); 
}

.threadbit.hot .threadstatus { 
    background-image: url("data:image/png;base64,"); 
}

.threadbit.hot.new .threadstatus{ 
    background-image: url("data:image/png;base64,"); 
}

.threadbit.dot.hot.new .threadstatus { 
    background-image: url("data:image/png;base64,"); 
}

.threadbit.dot .threadstatus {
    background-image: url("data:image/png;base64,");}

.threadbit.dot.new .threadstatus { 
    background-image: url("data:image/png;base64,"); 
}
}
