css:
@-moz-document url-prefix("https://mokum.place/") {
@font-face {
    font-family: Shabnam;
    src: url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2);
    src: url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2#iefix) format('embedded-opentype'),
         url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2) format('woff'),
         url(https://rastikerdar.github.io/shabnam-font/Shabnam.woff?v=0.7.2) format('truetype');
    font-weight: normal;
  }    
body{
    font-family:'Shabnam';
    background: #E4E4E4;
}

/****** TAHOMA ***/
.post-body .info,.btn,label,#attachments-button,.timeline-post-container .post-body .title,.sidebar,.author,.box-header-timeline,.stats,.comments .body a{
    font-family:tahoma;
}
/****** ANIMATION ***/
.sidebar,.more-comments{
	-webkit-transition: all 0.2s ease;
    -moz-transition: all 0.2s ease;
    -ms-transition: all 0.2s ease;
    -o-transition: all 0.2s ease;
    transition: all 0.2s ease;
}
.timeline-post-container .post-body .title{
    font-size:13px;
}
.timeline-post-container .post-body .title a{
    font-weight:normal;
}
.header .title{
    margin-right:0;
    margin-left:100px;
}
.header .title h1 a{
    background:url('http://i63.tinypic.com/4s17io.jpg') no-repeat;
    width: 150px;
    text-indent:-999px;
    display:block;
}
.box{
    margin-top:10px;
}
.box-header-timeline{
    margin-left:0;
    font-size: 125%;
    
}
.content .box-body {
    padding: 15px;
    background-color: #fff;
    box-shadow: 0px 2px 3px rgba(0,0,0,0.1);
}
.sidebar{
    opacity:0.4;
}
.sidebar:hover{
    opacity:1;
}
.sidebar .box-body{
    padding:10px;
    box-shadow: 0px 2px 3px rgba(0,0,0,0.1);
}
.sidebar .box-body ul li a {
    display: block;
    padding: 4px;
}
.sidebar .box-body ul li a:hover{
    background:#EBF0F1;
    text-decoration:none;
    
}

.timeline-post-container .avatar img{
	border-radius:150px;
    border:none;
}

.timeline-post-container .post-body{
    margin-left:0;
    margin-right:63px;
}

.comments_div .comment{
    padding:6px;
    border-radius:3px;
}
.comments_div .comment .body{
    margin-bottom:0;
}

.comments_div .comment:nth-child(2n){
    background:#EBF0F1;
}
.more-comments{
    display:block;  
    padding-right:5px;
}

.more-comments:hover{
    padding-right:20px;
}
.comment img{
    width:21px;
    height:21px;
	padding:3px;
    border-radius:50px;
}
.comments .comment div.body{
    margin-right:25px;
    margin-left:0;
}
.comments img:not([src^="/assets/comment-unknown-"]){
    background:#BBEFCE;
    
}
.comments img[src^="/assets/comment-unknown-"]{
    background:#FBCCC4;
    
}
/***** RTL ***/
.post-body,
.edit-post-area,
.search input{
    direction:rtl;
}

/***** LTR NO ALIGN ***/

.post-body .likes,
.timeline-post-container .post-body .title,
.more-comments{
    direction:ltr;
}

/***** TEXT-ALIGN: LEFT ***/


/***** TEXT-ALIGN: RIGHT ***/
.timeline-post-container .post-body .title,.more-comments{
    text-align:right;
}



.post-body .info{
	direction:ltr;
    text-align:left;
}


/***** FLOAT ***/

.comments .comment a.date,
.timeline-post-container .avatar,
.header .title{
    float:right;
}


/***** RESPONSIVE ***/

@media (min-width: 768px){
    .col-sm-1, .col-sm-2, .col-sm-3, .col-sm-4, .col-sm-5, .col-sm-6, .col-sm-7, .col-sm-8, .col-sm-9, .col-sm-10, .col-sm-11, .col-sm-12{
       float: right;
    }
}

@media (min-width: 992px){
    .col-md-1, .col-md-2, .col-md-3, .col-md-4, .col-md-5, .col-md-6, .col-md-7, .col-md-8, .col-md-9, .col-md-10, .col-md-11, .col-md-12 {
       float: right;
    }
}


@media (min-width: 1200px){
    .container {
       width: 1200px;
    }
}
}
