css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("msdn.microsoft.com"), domain("dev.windows.com"), domain("microsoft.com"), domain("live.com") {
  /* ============ Default Reset ============ */
  
  * { background-color: transparent !important; } /* */
  
  * { border-color: #00ACAF !important; color: #777 !important; box-shadow: none !important; text-shadow: none !important; } /* */
  a:link { color: #00ACAF !important; } /* */
  a:visited { color: #00ACCF !important; } /* */
  
  /* ======== popups ======== */
  div#divNRMain { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  
  /* ============ Site Settings ============ */
  
  html, body { background-color: #000C17 !important; }
  #BodyBackground.ltr { border: 0px solid transparent !important; }
  .contentTableWrapper { background-color: #000C17 !important; }
  #vsPanel { background-color: #000C17 !important; }
  img[src="https://msdn.microsoft.com/dynimg/IC820563.png"] { padding-left: 64px !important; padding-top: 64px !important; background: transparent url("http://i.imgur.com/EeS0HdV.png") no-repeat !important; width: 0px !important; height: 0px !important; background-size: contain !important; }
  
  #breakdown .iot-item .internet h3 { color: #000C47 !important; }
  .stdr-vote.stdr-content { background-color: #000C27 !important; }
  
  .shell-header-dropdown-content { background-color: #000C17 !important; border: 1px solid #000C47 !important; } /* top nav - menu */
  #shell-header .shell-header-nav li a { color: #00AC7F !important; } /* top nav - primary text */
  #shell-header .shell-header-nav li .shell-header-dropdown-content a { color: #00AC7F !important; } /* top nav - item text */
  #shell-header .shell-header-nav .shell-header-dropdown-tab-list li > a:hover { color: #00ACAF !important; background-color: #000C47 !important; }
  .shell-header-dropdown-tab.active { background-color: #000C27 !important; } /* top nav - selected item */
  .shell-header-dropdown-tab-content { background-color: #000C27 !important; border: 1px solid #000C47 !important; top: -1px !important; } /* top nav - sub menu */
  
  .subNav { background-color: #000C47 !important; }
  .officeSupports::after { background-color: #000C47 !important; }
  .subNav div ul li:hover { background-color: #000C67 !important; }
  
  .shell-search button:hover { background-color: #000C47 !important; } /* search button */
  #cli_searchSuggestionsResults { background-color: #000C17 !important; } /* search suggestions drop-down */
  #shell-header #cli_searchSuggestionsResults li.sg_term.selected { background-color: #000C47 !important; } /* search suggestions hover */
  
  .msame_Header { border: none !important; } /* sign in auth area */
  .msame_Header:hover { background-color: #000C37 !important; }
  
  .links.horizontal li a { color: transparent !important; }
  #Fragment_SiteLogo a span { color: transparent !important; }
  
  .shell-category-header { background-color: #000C27 !important; } /* nav header bar */
  
  .c-nav-dropdown-menu { background-color: #000C17 !important; border: 1px solid #000C47 !important; } /* nav header - menu */
  .c-top-nav-item:hover, .c-nav-dropdown-item:hover, .c-top-nav-link:hover { background-color: #000C37 !important; } /* nav header - item hover */
  .c-nav-item-with-dropdown.active { background-color: #000C37 !important;} /* nav header - flyout hover */
  .c-nav-dropdown-tab-content { background-color: #000C27 !important; border: 1px solid #000C47 !important; left: 268px !important; top: -1px !important; } /* nav header - sub menu */
  .shell-category-header .c-nav-dropdown-tab-content a:link { color: #777 !important; } /* nav header - sub menu - item */
  .shell-category-header .c-nav-dropdown-tab-content a:visited { color: #777 !important; }
  .shell-category-header .c-nav-dropdown-tab-content h3, .shell-category-header .c-nav-dropdown-tab-content h3 a:link { color: #00AC7F !important; } /* nav header - sub menu - missed text items */
  .c-nav-dropdown-tab-content li:hover { background-color: #000C37 !important; } /* nav header - sub menu - item hover */
  
  /* div#tocnav div.tocselected { background-color: #000C37 !important; } /* toc selected */
  #tocnav .toclevel:hover { background-color: #000C47 !important; } /* toc hover item */
  
  /* #mainSection .alert { background-color: #000C37 !important; } /* highlighted "note" items */
  
  .clsStd tr:hover { background-color: #000C37 !important; } /* table row highlight on hover */
  
  #ux-header header div.toc nav ul.navL2, #ux-header header div.toc nav ul.navL3 { background-color: #000C17 !important; border-top: 1px solid #00ACAF !important; }
  #ux-header header div.toc nav ul.navL3 { top: -1px !important; }
  #ux-header header div.toc nav ul.navL2 > li > a:hover, #ux-header header div.toc nav ul.navL3 > li > a:hover { background-color: #000C47 !important; }
  .breadCrumb > span > a, .breadCrumb > span > a:link, .breadCrumb > span > a:hover, .breadCrumb > span > a:visited { color: #777 !important; }
  .breadcrumbEllipsis > a, #tocExpandButton > a { background: transparent url("http://i.imgur.com/GifvBz6.png") no-repeat scroll !important; }
  #breadcrumbDropDownMenu { background-color: #000C27 !important; }
  #breadcrumbDropDownMenu ul li:hover { background-color: #000C47 !important; }
  #ux-header header div.row div.SearchBox form#HeaderSearchForm input { border: 1px solid #00ACAF !important; }
  #SearchFlyoutContainer { background-color: #000C17 !important; }
  #ux-header #SearchFlyoutContainer { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  #SearchFlyoutContainer #SuggestionContainer ul li[style*="background-color: rgb(238, 238, 238);"]:hover { background-color: #000C67 !important; color: #00ACAF !important; }
  div#skinnyRating { background-color: #000C27 !important; border: 1px solid #00ACAF !important; }
  div#skinnyRating div.buttons button { background-color: #000C17 !important; }
  div#skinnyRating div.buttons button:hover { background-color: #000C47 !important; color: #00ACAF !important; }
  
  #MainContent .MetroSearchtextbox { border-color: #00ACAF !important; }
  
  .breadCrumb > span.breadcrumbDropSmall > a::after { background: transparent url("http://i.imgur.com/3bYPR1V.gif") no-repeat top left !important; height: 15px !important; }
  
  /* #searchBox { height: 40px !important; } /**/
  #HeaderSearchTextBox { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  /* #searchBox { background-color: #000C17 !important; border: 1px solid #00ACAF !important; } /* */
  .search-box-query .quicksearchbox, #header-courses { background-color: #000C17 !important; }
  .header-item.courses .carot { border-color: transparent transparent #00aCAF !important; }
  
  .msame_Drop_content { background-color: #000C17 !important; }
  
  /* content */
  h1, h2, h3 { color: #00ACAF !important; } /* paragraph titles */
  .topic a, .topic a:link, .topic strong, .topic em { color: #00AC7F !important; } /* bolded links - highlighted terms */
  .topic a:hover { background-color: #000C37 !important; }
  .topic pre strong, .topic pre em, .topic pre { color: orange !important; }
  .ResultDescription, .ResultItemRight { color: #777 !important; }
  
  /* filter bar */
  ul li a.with-drop { background-color: #000C17 !important; border-color: transparent !important; } /* bg of filter control */
  .filterbar .drop-box { background-color: #000C17 !important; top: 35px !important; } /* filter popup */
  span.ResultsMessage span, #ResultsMessageDiv .ResultsMessage { color: #777 !important; }
  .RefinementListContainer .HeaderContainerRefinement { color: #777 !important; }
  .refine { background-color: #000C27 !important; }
  
  /* flash box */
  .flash-cont .flash-box { background-color: #000C37 !important; }
  
  /* side nav */
  .tr_Right_Nav_Header_OnePSTemplate { background-color: #000C37 !important; }
  
  .LearnMoreNavigation { border-color: transparent !important; } /* learn more hide/compress */
  
  .hero-bg, #programs-hero { background-image: none !important; }
  
}
