css:
@-moz-document domain("superanimes.com") {
* {line-height: 1.3em !important; transition: 0.2s;}
* .normal_header a, * h2 a {line-height: 12px !important;}

body {
    width: 1060px;
}

html, body {
    color: #414141 !important;
    
    text-align: center;
    margin: auto !important;
    font-size: 13px !important;
    font-family: Segoe UI !important;
    text-transform: lowercase !important;
/*     background: url(http://colourlovers.com.s3.amazonaws.com/images/patterns/3518/3518772.png) repeat #eaeaea !important;  */
    background: url(http://colourlovers.com.s3.amazonaws.com/images/patterns/3602/3602612.png) #eaeaea repeat;
}

a img:hover {
    opacity: 0.8 !important;
}
td {
    font-size: 13px !important;
}
a,a:visited {
    color: #606060 !important;
    text-decoration: none !important;
    font-style: italic !important; 
    transition: 0.2s;
}
a:hover {
    color: #6bbfe3 !important;
    transition: 0.2s;
}
.link1 {
    color: #818081;
}
.link2 {
    color: #6a6a6a;
}
small {
    font-size: 10px !important;
}
.breadcrumb {
    font-size: 13px !important;
}
.page-common .ff-avenir, .breadcrumb, .ff-Verdana, .floatRightHeader {
    font-family: Segoe UI !important;
}

.adstopo, #topo, #googleSearch, #rodape, .iconChat, .EpsMenu, .MenuEpsTitulo {
    display: none !important;
}

.conteudoBox, .conteudoBox2, .conteudoBox3, .boxSocial,
.selectVideo, .feedBox, .boxVideoDes,
.conteudoBoxHome, .boxDestakCorpoImg, .boxDestakT, .boxDestakTImg, .boxDestak {
    background: #fcfcfc;
    border: none;
    margin: 0px 0px 0px 0px;
    padding: 5px;
}

.conteudoBoxHome, .conteudoBox, .conteudoBox2, .conteudoBox3 {
    margin-bottom: 10px;
}

.boxMenuEps, .menuEpsList, #corpoVideo {
 	background: #fcfcfc;   
    color: #aeaeae;
    border: none;
}

.boxBarraInfo, .tituloBox {
    background: #f4f4f4;
    color: #aeaeae;
    border: none;
}
.loadImg { display: none; }
#geral {
    margin-top: 77px;
    width: 100%;
    position: relative;
}

#lateral .conteudoBox {
 	padding-bottom: 10px;   
}



.conteudoBox2 span[style="margin:10px; color:#f2f2f2; font-size:14px;"] {
    color: #777 !important;
}

.boxTop10 {
    height: 23px;
}

.boxTop10 .star {
    background: inherit;
    color: #414141;
    margin: 5px !important;
}

.maisConteudo {
    border: none;
}

.maisConteudo span {
    background: #f4f4f4 !important;
    color: #aeaeae !important;
    margin-right: 0px !important;
    padding: 5px 0px !important;
    font-family: Segoe UI !important;
    font-style: italic !important;
    border: none !important;
    box-shadow: 0 0 2px rgba(65,65,65,0.5); 
    transition: 0.2s !important;
    font-size: 13px !important; 
}
.maisConteudo span:hover {
    background: #fcfcfc !important;
    transition: 0.2s;
}
.maisConteudo span img {
    display: none;
}
.selectVideo {
 	width: 90%;   
}
.selectVideo button, .btn-seasonal .btn-type {
    background: #f4f4f4 !important; color: #aeaeae !important;
    margin-right: 2px !important; padding: 5px 7px !important;
    font-family: Segoe UI !important; font-style: italic !important;
    border-bottom: 2px solid #f4f4f4 !important; border-radius: 3px !important;
    box-shadow: 0 0 2px rgba(65,65,65,0.5); transition: 0s !important;
    font-size: 13px !important; }

.selectVideo button:hover, .btn-seasonal .btn-type:hover {
    background: #6bbfe3 !important; color: white !important;
    font-family: Segoe UI !important; padding: 6px 7px 7px !important;
    border-bottom: none !important;
    border-radius: 3px !important;
    box-shadow: 0 0 2px rgba(65,65,65,0.5);
}



.MenuEpsTitulo::before {
    height: 13px;
	display: block;
    background: #f4f4f4;
    color: #aeaeae;
}
#boxVideo {
    height: 29px;
}

#boxVideo * {
    border: none !important;
}
.vjs-default-skin .vjs-play-progress {
    background: #6bbfe3 !important;
}
.vjs-current-time-display, .vjs-time-divider,
.vjs-duration-display{
	margin-top: 8px;              
}
.vjs-poster {
 	background-size: cover;   
}
.flutuaLeft img {
    float: left;
}

::-webkit-scrollbar {
	width: 0px;
}
}
