css:
@-moz-document domain("ask.fm") {
.topBar {
    height: 39px;
    line-height: 39px;
    background: #000;
    text-align: center;
    overflow: hidden;
}
.rsp-container {
    background-color: #000;
}
.rsp-row main-content util-clearfix{
    background-color: #000;
}
#contentArea{
    background-color: #000;
}
.streamItem-answer, .streamItem-question, .streamItem-singleAnswer{
    background-color: #232323;
}
.streamItemContent-question{
	background-color: #232323;
    color: #FFF;
    font-family:"Segoe UI";
}
a.questionersName{
    color: #FFFFFF;
}
.streamItemContent-answer{
	color: #FFFFFF;
    font-family:"Segoe UI";
}
.tabBar {
    background: #000;
}
#profileButtons {
	background-color: #232323;
}
.subTabBar-wide profileTabBar {
	background-color: #232323;
}
.profileTabAnswerCount{
	background-color: #232323;
    color: #FFFFFF;
}
a.active{
	background-color: #232323;
}

.rsp-gte-tablet{
	background-color: #232323;
}

.profileTabLikeCount{
	background-color: #232323;
}
.profileTabBar{
	background-color: #232323;
}
.profileTabBar.subTabBar-wide a{
	color: #FF643C;
}
.streamItem{
	border-bottom: 8px solid #000000;
}
.streamItemOptions {
    border-top: solid 1px #000000;
}
#askBox{
	color: #FF643C;
    background: #000000;
}
.side-section{
	background: #000000;
}
.checkbox:checked+label, .checkbox:not(:checked)+label {
    color: #FFF;
    cursor: pointer;
}
.listItemCheckbox.listItemCheckbox-checked:before, .checkbox:checked+label:before {
    background: #000000;
    border: 1px solid #FFFFFF;
    color: #fff;
}
.askfm-ad-slot askfm-ad-slot-300x250{
background: url(http://i.imgur.com/8p7C8Hi.png) no-repeat !important;
}

.askfm-logo icon-askfm-logo topBar-left{
	color:#FFE200;
}

.desktop-menu a {
    color: #FFE200;
}

.icon-askfm-logo:before{
    color: #FFE200;
}

.streamItemsAge.owner a {
    color: #FFFFFF;
}

.streamItemsAge {
    color: #FFFFFF;
}
}
