css:
/* Mokum.place farsi style Without Border
   V0.002 24/10/94
   By : Morteza Dalil
    */
@-moz-document url-prefix("https://mokum.place/") {
body{
direction: rtl;
    font-family: Tahoma;
    background: #EAEAEA;
    
    background-size: auto;
    background-attachment: fixed;
}
.content .box-body{
    background-color: #fff!important;
        background-color: #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}

.timeline-post-container {
    padding: 14px;
}
.post-body .likes .icon{
 float:right;   
}

.comments .comment a.date {

    float: right;

}

.sidebar{
    direction: ltr;
}

.timeline-post-container {
    padding: 14px;

    margin: 10px;}
.sidebar .box-body{
    padding: 10px 10px 3px 0;
}

.controls{
direction:ltr;
    text-align:right;
}
.box-header, .box-header-empty, .box-header-timeline, .box-header-friends, .box-header-groups {
    border: none;
}





.sidebar .box-body {
    background-color: #FFFFFF;

    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
 
div.box-header-timeline {
    font-size: 16px;
}

li.previous a, li.next a {
    background-color: #d1d1d1;
    color: #000000;
}

.box-header-friends {
    font-weight: bold;
    font-size: 15px;
    background: #289628;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 2px;
}
.box-header-groups {
    border: none;
    background: #FF8300;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 2px;
}
div.box-header-timeline {
    font-size: 16px;
    background: #5393D8;
    color: white;
    padding: 2px;
    direction: ltr;
    border-radius: 10px 10px 0 0;
        margin-left: 0px !important;
}
.timeline-post-container .avatar {
    float: right;
    margin-left: 15px;}
}
