css:
@-moz-document domain("www.phoronix.com")
{
  .content > a[href*="identi.ca/phoronix"],
  .content > a[href*="openbenchmarking.org"],
  .content > a[href*="phorogit.com"],
  .content > a[href*="phoromatic.com"],
  .content > a[href*="phoronix.com"],
  .content > a[href*="phoronix-test-suite.com"],
  .content > a[href*="twitter.com/phoronix"],
  .content > a[href*="twitter.com/#!/michaellarabel"]  {
    position: relative !important;
    padding-left: 16px !important;
  }
  
  .content > a[href*="identi.ca/phoronix"]::before,
  .content > a[href*="openbenchmarking.org"]::before,
  .content > a[href*="phorogit.com"]::before,
  .content > a[href*="phoromatic.com"]::before,
  .content > a[href*="phoronix.com"]::before,
  .content > a[href*="phoronix-test-suite.com"]::before,
  .content > a[href*="twitter.com/phoronix"]::before,
  .content > a[href*="twitter.com/#!/michaellarabel"]::before  {
    content: '';
    padding: 0;
    margin: 0;
    width: 12px;
    height: 12px;
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    background: url('https://www.phoronix.com/favicon.ico') no-repeat;
    background-size: 12px 12px;
  }
}
