css:
@-moz-document domain(mokum.place) {
  /**
* @Author: mokum.place/imorteza
* @Extended-from: https://userstyles.org/styles/123109/mokum-tahoma
* 
**/
    
    @font-face {
        font-family: Samim;
        src: url('//rastikerdar.github.io/samim-font/Samim.eot?v=0.6.1');
        src: url('//rastikerdar.github.io/samim-font/Samim.eot?v=0.6.1#iefix') format('embedded-opentype'), url('//rastikerdar.github.io/samim-font/Samim.woff?v=0.6.1') format('woff'), url('//rastikerdar.github.io/samim-font/Samim.ttf?v=0.6.1') format('truetype');
        font-weight: normal;
    }
    @font-face {
        font-family: Samim;
        src: url('//rastikerdar.github.io/samim-font/Samim-Bold.eot?v=0.6.1');
        src: url('//rastikerdar.github.io/samim-font/Samim-Bold.eot?v=0.6.1#iefix') format('embedded-opentype'), url('//rastikerdar.github.io/samim-font/Samim-Bold.woff?v=0.6.1') format('woff'), url('//rastikerdar.github.io/samim-font/Samim-Bold.ttf?v=0.6.1') format('truetype');
        font-weight: bold;
    }
    * {
        font-family: Samim;
        font-size: 14px;
    }
    .box-header,
    .box-header-empty,
    .box-header-timeline,
    .box-header-friends,
    .box-header-groups {
        border: none;
        direction: rtl;
    }
    body {
        background: url("https://web.archive.org/web/20150407080109/http://friendfeed.com/static/images/themes/flowers/bottom-left.png?v=77b3") no-repeat bottom left;
        background-color: #e1dde9;
        background-attachment: fixed;
    }
    .content .box-body {
        border: solid 1px #e0e0e0;
        border-radius: 10px;
        padding-bottom: 15px;
        padding-left: 15px;
        padding-right: 15px;
        padding-top: 15px;
        line-height: 20px;
    }
    .sidebar .box-body {
        background-color: #ffffff;
        border-radius: 0px 0px 10px 10px;
        padding-bottom: 15px;
        padding-left: 15px;
        padding-right: 15px;
        padding-top: 15px;
    }
    .sidebar .box-header-friends,
    .sidebar .box-header-groups {
        border-radius: 10px 10px 0 0;
        background-color: #664082;
        color: #ffffff;
        text-align: left;
        padding-left: 10px;
    }
    .sidebar .box-body ul li a {
        color: #664082;
    }
    li.previous a,
    li.next a {
        background-color: #d1d1d1;
        color: #000000;
    }
    .comments {
        direction: rtl;
        text-align: left;
    }
    .comments .comment {
        margin-top: 10px;
        line-height: 18px;
    }
    .comments .comment div.body {
        direction: rtl;
    }
    .content a {
        color: #463C90;
    }
    .header .title {
        
    }

	.header .title a {
		width: 260px;
        background-image: url('https://mokum.place/system/attachments/000/030/508/mokum-thumb-bfc55ff7e2a1d194564a8ec7d184eb69998e5516.png');
        background-repeat: no-repeat;
        text-indent: -9999px;
        white-space: nowrap;
        margin-top: 20px;  
        display: block;
        height: 55px; 
    }

    .header .search {
        margin-top: 30px;
    }
    .timeline-post-container .post-container {
        border-bottom-color: #ccc;
    }
    .timeline-post-container .post-body .text {
        line-height: 1.8em;
        direction: rtl;
    }
}
