css:
@-moz-document url-prefix("https://ask.fm/") {
#topMenu {
border-bottom: 0px solid #ffc2c2;
}
  
.btn-action-icon {
background:#3d34b3;
color:#f0e0e6;
}

#profileBio {
border-top:solid 0;
color:#2f2f2f;
background:#A693AD;
padding:15px;
}

.tabBar {
background:#A693AD;
height:45px;
}

a.tabBarTab.active {
color:#2f2f2f;
height:32px;
border-bottom:3px solid #3d34b3;
}

.gemini-container {
display:none;
}

.gemini-block {
border-bottom:0;
}

.btn-secondary,.btn-action {
background:#2d2d2d;
border:0;
color:#f0e0e6;
padding:5px 8px;
}

.desktop-menu a.active {
border-bottom:solid 3px #3d34b3;
color:#2f2f2f;
}

.checkbox:checked + label,.checkbox:not(:checked) + label {
color:#2d2d2d;
cursor:pointer;
}

.likeButton:hover .icon-like:hover {
color:#3333FF;
}

.btn-primary:hover,.btn-primary-wide:hover,.btn-primary-icon:hover {
text-decoration:underline;
}

.viewMore:before,.askfm-ad-slot.loading:before,.gemini-container .loader:before,.visualItemPlayIcon-youTube.loader:before,.visualItemPlayIcon-animatedGif.loader:before {
animation:fadein forwards,rotate .8s infinite linear;
border:4px solid #3d34b3;
}

#answerForm .question {
color:#2f2f2f;
font-size:16px;
border-color:#3d34b3;
}

#askForm .question,.simpleFormSection-separator,#MassAskSearch {
border:0;
}

#askForm textarea,#streamItemAskForm input,#answerForm textarea {
background:#fabdbd;
color:#2f2f2f;
font-size:14px;
padding:5px;
}

#lightboxHead {
background:#A693AD;
color:#292929;
}

#newItemsReady {
background:#A693AD;
color:#3d34b3;
border-color:#A693AD;
}

#profileTabs {
background:#FADDE1;
border:0;
}

#searchResults .panelButton {
border-radius:2px;
float:right;
font-size:14px;
height:18px;
line-height:18px;
margin:5px;
padding:6px 10px;
}

#user_login.simpleForm,#user_email.simpleForm {
background:#ffc2c2;
color:#2f2f2f;
}

.afm-popover {
background:#ffc2c2;
border:solid 2px #292929;
box-shadow:0 0 0;
margin:0;
padding:0;
}

.afm-popover a:hover {
background:836e8b;
color:#2f2f2f;
margin:0;
}

.blackHeadline {
background:#FADDE1;
color:#3d34b3;
border-color:#3d34b3;
}

.blockLink.head.slim {
color:#2f2f2f;
font-weight:500;
}

.copyright {
color:1f1f1f;
}

.defaultListItem,.socialListItem,#sidebarMenu a {
background:#ffc2c2;
border-bottom:1px solid #292929;
color:#292929;
}

.emptyViewContent a.profile-share,a.inboxItem-answerButton {
background:#3d34b3;
color:#ffc2c2;
}

.emptyViewContent p,.streamItemContent-answer,.desktop-menu a,.icon-flag,.icon-block,a.profileTab,a.profileTab strong,.fileUploadLevel.error {
color:#292929;
}

.fileUploadContainer-answerPictureSettings {
background-color:#ffc2c2;
background-position:10px 0;
}

a.profilePictureSettings-option:hover {
color:#3d34b3;
text-decoration:underline;
}

.askfm-ad-slot.loading {
position:relative;
display:none;
}

.fileUploadContainerContent {
background:#FADDE1;
margin:0;
padding:10px;
}

.fileUploadInput {
color:#292929;
font-size:12px;
}

.fileUploadSubmit {
background:#3d34b3;
color:#2f2f2f;
}

.headlineCell,.subTabBar a,.topBarCancel,.simpleFormItem-hint {
background:#ffc2c2;
color:#292929;
}

.panelButton,.optionsBar-submit,a.askFriendListItem,.topBarSubmit,a.baseButton-green,a.askFriendListItem,a.fontMenuButton-green {
color:#ffc2c2;
}

.profileLabel,a.baseButton-1f1f1f {
background:none;
color:#2f2f2f;
}

.searchBar,.gifts-container,.dialog-hint,.text-paragraph,a.button-1f1f1f {
background:#FADDE1;
color:#292929;
}

.selectForm {
background:#fabdbd;
border:0;
color:#2f2f2f;
font-size:14px;
padding:10px;
}

.side-column #askForm .question {
background-color:#fabdbd;
border:0;
padding:0;
}

.side-section {
background:#FADDE1;
color:#2d2d2d;
}

.simpleForm {
background:#fabdbd;
color:#2f2f2f;
}

.simpleFormLabel {
background:none;
color:#292929;
font-size:14px;
font-weight:700;
}

.simpleFormPadding {
background:#fabdbd;
border:0;
padding:5px;
}

.streamItem,#answerForm .answer {
border-bottom:8px solid #ffc2c2;
}

.streamItem-notification.unread {
background:#fabdbd;
}

.streamItemContent-question {
color:#2f2f2f;
font-size:16px;
font-weight:500;
line-height:16px;
}

.streamItemWallContent {
margin-left:75px;
}

.streamItemAvatar {
border-radius:50%;
background-size:40px 40px;
border:solid 0 #ffc2c2;
position:absolute;
top:8px;
}

.streamItemLike {
color:#3333FF;
font-size:12px;
margin-left:75px;
}

.streamItemContent-answer {
position:relative;
}

.streamItemOptions,.side-column h3 + .streamItem {
border-color:#3d34b3;
}

.streamItem.streamItem-user,.streamItem.streamItem-notification,.streamItem.streamItem-shareAnswer,.streamItem.streamItem-massQuestion,.streamItem.blockListItem {
border-bottom:1px solid #3d34b3;
margin-bottom:0;
}

.subTabBar a.active {
color:#f0e0e6;
}

.text-heading {
background:#ffc2c2;
color:#3d34b3;
}

a.baseButton-1f1f1f:hover {
background:#292929;
color:#ffc2c2;
}

a.inboxItem-answerButton:hover {
background:#f5b1a6;
color:#f0e0e6;
}

.profileTabBar.subTabBar-wide a {
color:#2d2d2d;
background:#A693AD;
line-height:1;
border-bottom:0;
}

.subTabBar-wide a.active {
background:#FADDE1;
border-bottom:3px solid #3d34b3;
color:#2f2f2f;
}

.profileTabBar {
border-bottom:0 solid #ffc2c2;
border-top:0 solid #ffc2c2;
}

.subTabBar-wide {
background:#A693AD;
line-height:45px;
}

.subTabBar-wide a {
border-bottom:1px solid #A693AD;
}

a.socialListItem:hover {
background-color:#A693AD;
}

body {
font-family:"Segoe UI";
}

body.contentBody,.streamItem-notification:hover,.simpleFormItem,#MassAskSearch .streamItem-user.listItemCheckbox-checked {
background:#ffc2c2;
}

body.welcomeBody {
background:#715c79;
}

h2.dialog-block {
background:#FADDE1;
color:#2f2f2f;
font-weight:600;
}

h3 {
background:#FADDE1;
color:#3d34b3;
font-weight:500;
}

footer {
background-color:#ffc2c2;
}

.btn-secondary:hover,.btn-action:hover,.btn-primary,.btn-primary-wide,.btn-primary-icon {
background:#3d34b3;
color:#f0e0e6;
}

.desktop-menu a:hover,a.profilePictureSettings-option,#desktop-menu-icon a:hover,#desktop-menu-icon a.active,.profileTabBar.subTabBar-wide a div {
color:#2f2f2f;
}

#streamItemAskForm,.emptyViewContent,.topBar,body.dialogBody .main-column,.top-content,.streamItem,#askForm,.optionsBar {
background:#FADDE1;
}

#streamItemAskForm .question,.simpleFormPadding-disabled {
background:#ffc2c2;
border:0;
padding:0;
}

.btn-action-icon.icon-back,.btn-action-icon.icon-hamburger,a,.streamItemContent-info {
color:#3d34b3;
}

.aboutMore.more .moreButton {
background:#A693AD;
}

a.tabBarTab,.blockLink.slim.foot,.likeButton .counter,#desktop-menu-icon a,footer .site-links a,.searchForm {
color:#2d2d2d;
}
