css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mokum.place") {

  .box-header, .box-header-empty, .box-header-timeline, .box-header-friends, .box-header-groups {
    border: none;
}

body {
    background: #9bb4c5 url("https://mokum.place/system/attachments/000/032/204/mokum-medium-3a1f91658b2a42d00ae1d33948525f0be3d1d37d.png") repeat-x scroll center top;
    background-size: auto;
    background-attachment: fixed;
  }

.content .box-body {
    
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}

.sidebar .box-body {
    background-color: #F1F2F1;
    border-radius: 0px 0px 10px 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
    .sidebar .box-header-friends{
        background-image: url("https://mokum.place/system/attachments/000/032/205/mokum-medium-e9361bda18b9b9de647529a440b5415f59bba8b8.png");
        border-radius: 10px 10px 0 0;
        color: #ffffff;
        text-align: left;
        padding-left: 10px;

    }
    
    .sidebar .box-header-groups {
          background-image: url("https://mokum.place/system/attachments/000/032/212/mokum-medium-872e52be1e9a1391df278c5e389f184b219a98d6.png");
        border-radius: 10px 10px 0 0;
        color: #ffffff;
        text-align: left;
        padding-left: 10px;

    }

    
.sidebar .box-body ul li a {
    color: #0000cc;
    font-size: 15px;
}    

div.box-header-timeline {
        background-image: url("https://mokum.place/system/attachments/000/032/213/mokum-medium-958a2f4d2154d22e1c2b86fcf3633b9115cca258.png");
    

    font-size: 14px;
     border-radius: 10px 10px 0 0;
        color: #ffffff;
        text-align: left;
        padding-left: 10px;
}

.box-header-timeline {
    font-size: 170%;
    font-weight: 700;
    margin-left: 0px;
}


.header .title{
    width:228px;
    background-image:url('https://mokum.place/system/attachments/000/047/212/mokum-medium-6f9a4e42450714a9293f908da4d07de7cd092bb3.png');
    background-repeat:no-repeat;
    margin-top:25px;
}

div.title h1 {
     opacity: 0;
}
.header .search{
    margin-top:40px;
}
    
    .content .box-footer {
    background-color: #fff;
    font-size: 11px;
    padding: 5px 0 5px 11px;
        border-radius: 0px 0px 10px 10px;
}
.post-body {
    color: #666;
}
.post-body a {
    color: #7777cc;
}
.post-body .title {
    font-size: 15px;
    margin-bottom: 2px;
}
.post-body .title a {
    color: #0000cc;
    font-weight: bold;
}
.post-body .body {
    margin-bottom: 8px;
}
.post-body .body .text {
    color: #000;
}
.post-body .body .text a {
    color: #0000cc;
}

}
