css:
@-moz-document url-prefix("http://ark.franckrst.com/") {
.googleAdFull {
    display: none;
}

body {
    background-color: #fff;
    background-image: none !important;
    /*background-image: url(../image/bg.jpg) !important;*/
    font-family: Helvetica, sans-serif;
}

.btn-return {
    display: none;
}
h1.ng-scope {
    display: none;
}
#descriptionMap {
    display: none;
}
#share-site {
    display: none;
}
#center {
    margin: 0;
}
#map {
    background-image: url("http://i.imgur.com/Mfpor91.jpg");
}
#mapContent.big-map #map {
    background-image: url("http://i.imgur.com/bhxF7Xs.jpg");
}

md-input-container {
    padding: 0px;
}
.md-button {
	margin: 16px 8px 6px 6px
}
.k-picker-wrap .k-select {
	min-height: 100%;
}

md-list-item.md-2-line .md-list-item-text, md-list-item.md-2-line>.md-no-style .md-list-item-text, md-list-item.md-3-line .md-list-item-text, md-list-item.md-3-line>.md-no-style .md-list-item-text {
    padding: 15px 0 0 0;
}

md-list-item .md-secondary {
    margin-left: 0px;
    position: absolute;
    right: -5px;
    top: -5px;
    -webkit-transform: translate3d(0,0,0);
    transform: translate3d(0,0,0);
}

#editor .taille {
    position: fixed;
}

#editor h2 {
    font-size: 14px;
    margin-top: 15px;
    font-weight: 400;
    border-bottom: 5px solid #eee;
}

.md-button.md-default-theme {
    border-radius: 0px;
}
.md-button {
    margin: 0;
}

span.ng-binding.ng-scope {
    background-color: #FFF;
}
}
