css:
@-moz-document url-prefix("https://tweetdeck.twitter.com/"), url-prefix("https://web.tweetdeck.com/") {
@font-face {
  font-family: "Determination";
  src: local('Determination'), url(data:font/woff;charset=utf-8;base64,) format('woff');
}

html {
	font-family: "Determination", Determination, Helvetica, Arial, Verdana, sans-serif !important;
    font-size: 16px !important;
    font-weight: normal !important;
    -webkit-font-smoothing: none !important;
    text-rendering: unset !important;
}

.mdl, .js-modal-inner, .js-modal-panel, .stream-item, .js-column-header, .is-inverted-dark, .item-box {
	font-family: "Determination", Determination, Helvetica, Arial, Verdana, sans-serif !important;
    font-size: 16px !important;
    font-weight: normal !important;
}

.js-column-drag-handle {
	background: none !important;
	height: 100%;
	width: 100%;
}

.column {
	margin-right: 0 !important;
	border: 0;
    font-size: 16px !important;
    font-weight: normal !important;
}

.txt-bold, .fullname, .txt-small, .column-title, .txt-mute, .js-drawer, .tooltip, h1, .txt-ellipsis, .compose-text, .link-complex-target {
	font-size: 16px !important;
    font-weight: normal !important;
    -webkit-font-smoothing: none !important;
    text-rendering: unset !important;
}

.username {
	font-size: 16px !important;
    font-weight: normal !important;
}

#container {
    background-color: #222426;
}

.stream-item {
	border-bottom: none;
}

.app-columns {
	padding: 0 !important;
}

.scroll-styled-v, .scroll-styled-h {
	border-left: 0 !important;
}

.scroll-styled-v::-webkit-scrollbar-track, .scroll-styled-h::-webkit-scrollbar-track {
	border-left: 0;
}

.scroll-styled-v::-webkit-scrollbar {
	width: 7px !important;
}

.scroll-styled-v::-webkit-scrollbar-thumb,
.scroll-styled-h::-webkit-scrollbar-thumb {
  border-radius: 5px;
  background-color: rgba(255,255,255,0.05); }
  .scroll-styled-v::-webkit-scrollbar-thumb:hover,
  .scroll-styled-h::-webkit-scrollbar-thumb:hover {
    background-color: white; }

.column-type-icon {
	display: none;
}

.column-number {
	display: none;
}

.js-app-header-inner .icon-large {
	font-size: 20px;
	text-align: center;
}

.js-app-header-inner .tweet-button {
	border-radius: 20px;
}

.js-app-header-inner .attach-compose-buttons .tweet-button {
	border-top-left-radius: 20px;
	border-bottom-left-radius: 20px;
	border-top-right-radius: 0px;
	border-bottom-right-radius: 0px;
}

.icon-compose {
	font-size: 20px !important;
	margin: 0 !important;
}

.column-navigator {
	bottom: 170px !important;
}

.app-navigator {
	bottom: 30px !important;
}

.js-dropdown-content .is-selectable a {
	color: #111 !important;
}

#global-settings .mdl-links .inline-block:after {
	content: "Â· (It fills you with determination...)";
}

.nav-user-info {
	display: none;
}

.stream-item {
    border-bottom: 8px solid black;
    background: black;
    box-shadow: inset 0 0 0px 4px white;
}

.app-title {
	background: url(data:image/png;base64,);
}

.sprite-logo-badge {
	display: none !important;
}

.avatar {
	-webkit-filter: grayscale(1);
}

a {
	color: #999 !important;
}

.fullname {
	color: white;
}
}
