css:
@-moz-document domain("www.freecodecamp.com") {
    body, .map-aside-body, 
    .map-fixed-header, .modal-body{
        background: #111;
          color: #f6f6f6 
    }
    
    .cert-name, .cert-date {
        color: black;
    }
    input.big-text-field {
        background-color: #444 !important;
    }   
    h1,h2,h3,h4,h5,h6, .text-primary {
        color: #90C695;
    }
    a, a.fa {
        color: #407521;
    }
    
    a:hover, a.fa:hover, a:focus, a.fa:focus {
        color: #F5D76E ;
    }
    
    .map-aside-action-bar {
        background: none;
        background-color: #407521;
    }

    button.hamburger.navbar-toggle {
        color: #fff !important;
    }
    .btn, button{
        border-color: #407521 !important;
        color: #407521 !important;
        background-color: transparent !important;
    }
    
    .btn.disabled {
       background-color:   #90C695 !important;  
       color: #f6f6f6 !important;
    }
    
    td {color:#f6f6f6 !important;}
    
    .table-striped td {
        border: none !important;
    }
    .table-striped > tbody > tr:nth-child(2n+1) {
       background-color: #202020;
    }
    
    .btn:hover {
        color: #f6f6f6 !important;
        background-color: #407521 !important;
    }
    
    .cal-heatmap-container {
        background-color: #111;
    }
    
    hr {
       background-image: linear-gradient(to right, transparent, rgba(110, 110, 110, 0.75), transparent);
    }

     input, textarea {
         background-color: #292929 !Important;
         color: #f6f6f6 !important;
         border-color: #407521 !important;
     } 
    
    input:focus, textarea:focus {
        box-shadow: 0px 0px 3px #90C695 !important;
    }
    
    .navbar {background: #407521;}
    .navbar-right{background: #407521;}
    .panel-heading{
        background: #407521 !important;
    	border-color:#407521 !important; 
    }
    .panel-info{border-color:#407521;}
    .panel-body{background-color:#222}
    td {color:white !important;}
    .cm-s-monokai.CodeMirror{background-color:#1D1D1D !important;}
    #scroll-locker {background: #1D1D1D; color: #F8F8F2;}
    code {background: #1D1D1D; color: #AE7FDA;}
    #submitButton {
        border-left-color: #AE7FDA; 
        border-right-color: #AE7FDA; 
        border-top-color: #AE7FDA;
        border-bottom-color: #AE7FDA;
        color: #AE7FDA;
    }
    #trigger-reset-modal{
        border-left-color: #AE7FDA; 
        border-right-color: #AE7FDA; 
        border-top-color: #AE7FDA;
        border-bottom-color: #AE7FDA;
        color: #AE7FDA;
    }
    #challenge-help-btn{
        border-left-color: #AE7FDA; 
        border-right-color: #AE7FDA; 
        border-top-color: #AE7FDA;
        border-bottom-color: #AE7FDA;
        color: #AE7FDA;
    }
    #trigger-issue-modal{
        border-left-color: #AE7FDA; 
        border-right-color: #AE7FDA; 
        border-top-color: #AE7FDA;
        border-bottom-color: #AE7FDA;
        color: #AE7FDA;
    }
    #mainEditorPanel{background: #0c0c0c !important; }
    body.no-top-and-bottom-margins {background:#0c0c0c !important;}
}
